import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, Button, Spinner, Nav, Tab, Badge, Alert } from 'react-bootstrap';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import ReportList from '../reports/ReportList';
import api from '../../services/api';

const UserDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalReports: 0,
    pendingReports: 0,
    inProgressReports: 0,
    resolvedReports: 0
  });
  const [activeTab, setActiveTab] = useState('all');
  const [loading, setLoading] = useState(true);
  const [recentActivity, setRecentActivity] = useState([]);

  useEffect(() => {
    const fetchDashboardData = async () => {
      try {
        // Fetch user reports for stats calculation
        const reportsResponse = await api.get(`/reports?userId=${user?._id}&limit=10`);
        const reports = reportsResponse.data || [];

        // Calculate stats from fetched reports
        const calculatedStats = {
          totalReports: reports.length,
          pendingReports: reports.filter(r => r.status === 'pending').length,
          inProgressReports: reports.filter(r => r.status === 'in-progress').length,
          resolvedReports: reports.filter(r => r.status === 'resolved').length
        };

        setStats(calculatedStats);
        setRecentActivity(reports.slice(0, 3)); // Last 3 reports for activity feed
      } catch (error) {
        console.error('Failed to fetch dashboard data:', error);
        // Set default values to prevent crashes
        setStats({
          totalReports: 0,
          pendingReports: 0,
          inProgressReports: 0,
          resolvedReports: 0
        });
      } finally {
        setLoading(false);
      }
    };

    if (user?._id) {
      fetchDashboardData();
    }
  }, [user?._id]);

  const getProgressPercentage = () => {
    if (stats.totalReports === 0) return 0;
    return Math.round((stats.resolvedReports / stats.totalReports) * 100);
  };

  const formatDate = (dateString) => {
    const options = { month: 'short', day: 'numeric', hour: '2-digit', minute: '2-digit' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <div style={{ backgroundColor: '#f8f9fa', minHeight: '100vh' }}>
      {/* Modern Header */}
      <div className="bg-white border-bottom shadow-sm">
        <Container fluid className="px-4">
          <Row className="align-items-center py-3">
            <Col md={6}>
              <div className="d-flex align-items-center">
                <div 
                  className="rounded-circle d-flex align-items-center justify-content-center me-3"
                  style={{ 
                    width: '50px', 
                    height: '50px', 
                    background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)' 
                  }}
                >
                  <i className="fas fa-user text-white"></i>
                </div>
                <div>
                  <h1 className="h4 mb-0 fw-bold" style={{ color: '#2c3e50' }}>
                    Welcome back, {user?.name || 'User'}!
                  </h1>
                  <p className="text-muted mb-0 small">
                    {new Date().toLocaleDateString('en-US', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}
                  </p>
                </div>
              </div>
            </Col>
            <Col md={6} className="text-md-end">
              <div className="d-flex gap-2 justify-content-md-end mt-3 mt-md-0">
                <Button 
                  variant="outline-primary" 
                  size="sm"
                  className="rounded-pill"
                  onClick={() => navigate('/profile')}
                >
                  <i className="fas fa-user-cog me-1"></i>
                  Profile
                </Button>
                <Button 
                  variant="primary" 
                  size="sm"
                  className="rounded-pill"
                  style={{ backgroundColor: '#80ACC2', border: 'none' }}
                  onClick={() => navigate('/report-form')}
                >
                  <i className="fas fa-plus me-1"></i>
                  Report Issue
                </Button>
              </div>
            </Col>
          </Row>
        </Container>
      </div>

      <Container fluid className="px-4 py-4">
        {/* Stats Overview */}
        <Row className="mb-4">
          <Col lg={8}>
            <Row>
              <Col md={6} className="mb-3">
                <Card className="border-0 h-100 modern-stat-card">
                  <Card.Body className="p-4">
                    <div className="d-flex justify-content-between align-items-start">
                      <div>
                        <p className="text-muted mb-1 small">TOTAL REPORTS</p>
                        <h3 className="mb-0 fw-bold" style={{ color: '#2c3e50', fontSize: '2rem' }}>
                          {loading ? <Spinner animation="border" size="sm" /> : stats.totalReports}
                        </h3>
                      </div>
                      <div className="rounded-circle p-3" style={{ backgroundColor: '#e8f4fd' }}>
                        <i className="fas fa-clipboard-list text-primary" style={{ color: '#80ACC2', fontSize: '1.2rem' }}></i>
                      </div>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
              <Col md={6} className="mb-3">
                <Card className="border-0 h-100 modern-stat-card">
                  <Card.Body className="p-4">
                    <div className="d-flex justify-content-between align-items-start">
                      <div>
                        <p className="text-muted mb-1 small">RESOLUTION RATE</p>
                        <h3 className="mb-0 fw-bold" style={{ color: '#27ae60', fontSize: '2rem' }}>
                          {loading ? <Spinner animation="border" size="sm" /> : `${getProgressPercentage()}%`}
                        </h3>
                      </div>
                      <div className="rounded-circle p-3" style={{ backgroundColor: '#d4edda' }}>
                        <i className="fas fa-chart-line text-success" style={{ color: '#27ae60', fontSize: '1.2rem' }}></i>
                      </div>
                    </div>
                  </Card.Body>
                </Card>
              </Col>
            </Row>

            {/* Progress Bar */}
            <Card className="border-0 mb-3">
              <Card.Body className="p-4">
                <h5 className="mb-3">Report Status Overview</h5>
                <div className="mb-3">
                  <div className="d-flex justify-content-between mb-2">
                    <span className="small">Pending</span>
                    <span className="small fw-bold">{stats.pendingReports}</span>
                  </div>
                  <div className="progress" style={{ height: '8px' }}>
                    <div 
                      className="progress-bar bg-warning" 
                      style={{ 
                        width: `${stats.totalReports > 0 ? (stats.pendingReports / stats.totalReports) * 100 : 0}%`,
                        backgroundColor: '#f39c12'
                      }}
                    ></div>
                  </div>
                </div>
                <div className="mb-3">
                  <div className="d-flex justify-content-between mb-2">
                    <span className="small">In Progress</span>
                    <span className="small fw-bold">{stats.inProgressReports}</span>
                  </div>
                  <div className="progress" style={{ height: '8px' }}>
                    <div 
                      className="progress-bar bg-info" 
                      style={{ 
                        width: `${stats.totalReports > 0 ? (stats.inProgressReports / stats.totalReports) * 100 : 0}%`,
                        backgroundColor: '#3498db'
                      }}
                    ></div>
                  </div>
                </div>
                <div>
                  <div className="d-flex justify-content-between mb-2">
                    <span className="small">Resolved</span>
                    <span className="small fw-bold">{stats.resolvedReports}</span>
                  </div>
                  <div className="progress" style={{ height: '8px' }}>
                    <div 
                      className="progress-bar bg-success" 
                      style={{ 
                        width: `${stats.totalReports > 0 ? (stats.resolvedReports / stats.totalReports) * 100 : 0}%`,
                        backgroundColor: '#27ae60'
                      }}
                    ></div>
                  </div>
                </div>
              </Card.Body>
            </Card>
          </Col>

          {/* Quick Actions */}
          <Col lg={4}>
            <Card className="border-0 h-100">
              <Card.Body className="p-4">
                <h5 className="mb-4">Quick Actions</h5>
                <div className="d-grid gap-2">
                  <Button 
                    variant="outline-primary" 
                    className="w-100 text-start"
                    onClick={() => navigate('/report-form')}
                  >
                    <i className="fas fa-plus me-2"></i>
                    New Report
                  </Button>
                  <Button 
                    variant="outline-secondary" 
                    className="w-100 text-start"
                    onClick={() => navigate('/my-reports')}
                  >
                    <i className="fas fa-list me-2"></i>
                    View All Reports
                  </Button>
                  <Button 
                    variant="outline-secondary" 
                    className="w-100 text-start"
                    onClick={() => navigate('/notifications')}
                  >
                    <i className="fas fa-bell me-2"></i>
                    Notifications
                  </Button>
                  <Button 
                    variant="outline-secondary" 
                    className="w-100 text-start"
                    onClick={() => navigate('/help')}
                  >
                    <i className="fas fa-question-circle me-2"></i>
                    Help Center
                  </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Recent Activity & Reports */}
        <Row>
          <Col lg={8}>
            <Card className="border-0">
              <Card.Header className="bg-white border-bottom">
                <Nav variant="tabs" activeKey={activeTab} onSelect={(k) => setActiveTab(k)}>
                  <Nav.Item>
                    <Nav.Link eventKey="all">All Reports</Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="pending">Pending</Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="in-progress">In Progress</Nav.Link>
                  </Nav.Item>
                  <Nav.Item>
                    <Nav.Link eventKey="resolved">Resolved</Nav.Link>
                  </Nav.Item>
                </Nav>
              </Card.Header>
              <Card.Body className="p-4">
                <ReportList userId={user?._id} filter={activeTab} />
              </Card.Body>
            </Card>
          </Col>

          <Col lg={4}>
            <Card className="border-0">
              <Card.Header className="bg-white border-bottom">
                <h5 className="mb-0">Recent Activity</h5>
              </Card.Header>
              <Card.Body className="p-0">
                {recentActivity.length === 0 ? (
                  <div className="text-center p-4">
                    <i className="fas fa-history fa-2x text-muted mb-3"></i>
                    <p className="text-muted">No recent activity</p>
                  </div>
                ) : (
                  <div>
                    {recentActivity.map((activity, index) => (
                      <div key={activity._id} className={`p-3 ${index !== recentActivity.length - 1 ? 'border-bottom' : ''}`}>
                        <div className="d-flex justify-content-between align-items-start">
                          <div className="flex-grow-1">
                            <h6 className="mb-1 fw-bold">{activity.title}</h6>
                            <p className="text-muted small mb-1">
                              <i className="fas fa-map-marker-alt me-1"></i>
                              {activity.location}
                            </p>
                          </div>
                          <Badge 
                            bg={activity.status === 'resolved' ? 'success' : activity.status === 'in-progress' ? 'info' : 'warning'}
                            className="text-capitalize"
                          >
                            {activity.status.replace('-', ' ')}
                          </Badge>
                        </div>
                        <small className="text-muted">
                          <i className="far fa-clock me-1"></i>
                          {formatDate(activity.createdAt)}
                        </small>
                      </div>
                    ))}
                  </div>
                )}
              </Card.Body>
            </Card>
          </Col>
        </Row>
      </Container>

      <style jsx>{`
        .modern-stat-card {
          transition: transform 0.2s ease;
        }
        .modern-stat-card:hover {
          transform: translateY(-5px);
        }
      `}</style>
    </div>
  );
};

export default UserDashboard;
