import React, { useState } from 'react';
import { Container, Row, Col, Card, Tabs, Tab, Form, Button } from 'react-bootstrap';
import ReportList from '../reports/ReportList';
import { useAuth } from '../../context/AuthContext';

const MaintainerDashboard = () => {
  const { user } = useAuth();
  const [key, setKey] = useState('assigned');
  const [filter, setFilter] = useState({
    status: 'assigned'
  });

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter({ ...filter, [name]: value });
  };

  return (
    <Container>
      <Row className="mb-4" style={{ background: 'linear-gradient(135deg, rgb(103, 184, 200) 0%, rgb(86 110 111) 100%)' }}>
        <Col>
          <Card className="glass-card border-0">
            <Card.Body>
              <h1>Maintainer Dashboard</h1>
              <p className="text-muted">Manage assigned reports and track their progress</p>
              <div className="d-flex gap-2 mt-3">
                <button
                  type="button"
                  className="btn"
                  onClick={() => window.location.reload()}
                  style={{
                    backgroundColor: '#6e979d',
                    borderColor: '#6e979d',
                    color: 'white',
                    transition: 'all 0.3s ease'
                  }}
                  onMouseEnter={(e) => {
                    e.target.style.backgroundColor = '#8ba8ad';
                    e.target.style.borderColor = '#8ba8ad';
                  }}
                  onMouseLeave={(e) => {
                    e.target.style.backgroundColor = '#6e979d';
                    e.target.style.borderColor = '#6e979d';
                  }}
                >
                  Refresh Reports
                </button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row>
        <Col>
          <Card>
            <Card.Header as="h5">Reports</Card.Header>
            <Card.Body>
              <Tabs
                id="report-tabs"
                activeKey={key}
                onSelect={(k) => {
                  setKey(k);
                  setFilter({ ...filter, status: k });
                }}
                className="mb-3"
              >
                <Tab eventKey="assigned" title="Assigned Reports">
                  <ReportList />
                </Tab>
                <Tab eventKey="in_progress" title="In Progress">
                  <ReportList status="in_progress" />
                </Tab>
                <Tab eventKey="resolved" title="Resolved">
                  <ReportList status="resolved" />
                </Tab>

              </Tabs>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default MaintainerDashboard;
