import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import ReportList from '../reports/ReportList';
import api from '../../services/api';
import './Dashboard.css';

const UserDashboard = () => {
  const { user } = useAuth();
  const navigate = useNavigate();
  const [stats, setStats] = useState({
    totalReports: 0,
    pendingReports: 0,
    resolvedReports: 0
  });
  const [activeFilter, setActiveFilter] = useState('all');
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchStats = async () => {
      try {
        const response = await api.get('/reports/stats');
        setStats(response.data);
      } catch (error) {
        console.error('Failed to fetch stats:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchStats();
  }, []);

  return (
    <div className="dashboard-container">
      {/* Dashboard Header */}
      <div className="dashboard-header">
        <div className="welcome-section">
          <h1>Welcome, {user?.name || 'User'}!</h1>
          <p>Report and track issues in your campus</p>
        </div>
        <button 
          className="btn-primary"
          onClick={() => navigate('/report-form')}
        >
          <i className="fas fa-plus me-2"></i>
          Report New Issue
        </button>
      </div>

      {/* Dashboard Content */}
      <div className="dashboard-content">
        {/* Stats Grid */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon">
                <i className="fas fa-clipboard-list"></i>
              </div>
            </div>
            <div className="stat-value">{stats.totalReports}</div>
            <div className="stat-label">Total Reports</div>
          </div>

          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon">
                <i className="fas fa-clock"></i>
              </div>
            </div>
            <div className="stat-value">{stats.pendingReports}</div>
            <div className="stat-label">Pending Reports</div>
          </div>

          <div className="stat-card">
            <div className="stat-header">
              <div className="stat-icon">
                <i className="fas fa-check-circle"></i>
              </div>
            </div>
            <div className="stat-value">{stats.resolvedReports}</div>
            <div className="stat-label">Resolved Reports</div>
          </div>
        </div>

        {/* Reports Section */}
        <div className="reports-section">
          <div className="section-header">
            <h2 className="section-title">Your Reports</h2>
            <button 
              className="btn-primary"
              onClick={() => navigate('/report-form')}
            >
              <i className="fas fa-plus me-2"></i>
              New Report
            </button>
          </div>

          <div className="section-body">
            {/* Filter Tabs */}
            <div className="filter-tabs">
              <button 
                className={`filter-tab ${activeFilter === 'all' ? 'active' : ''}`}
                onClick={() => setActiveFilter('all')}
              >
                All Reports
              </button>
              <button 
                className={`filter-tab ${activeFilter === 'pending' ? 'active' : ''}`}
                onClick={() => setActiveFilter('pending')}
              >
                Pending
              </button>
              <button 
                className={`filter-tab ${activeFilter === 'in-progress' ? 'active' : ''}`}
                onClick={() => setActiveFilter('in-progress')}
              >
                In Progress
              </button>
              <button 
                className={`filter-tab ${activeFilter === 'resolved' ? 'active' : ''}`}
                onClick={() => setActiveFilter('resolved')}
              >
                Resolved
              </button>
            </div>

            {/* Report List */}
            {loading ? (
              <div className="d-flex justify-content-center p-4">
                <div className="spinner-border text-primary" role="status">
                  <span className="visually-hidden">Loading...</span>
                </div>
              </div>
            ) : (
              <ReportList userId={user?._id} filter={activeFilter} />
            )}

            {/* Quick Actions */}
            <div className="quick-actions">
              <a href="#" className="quick-action-btn">
                <i className="fas fa-download"></i>
                Export Reports
              </a>
              <a href="#" className="quick-action-btn">
                <i className="fas fa-bell"></i>
                Notification Settings
              </a>
              <a href="#" className="quick-action-btn">
                <i className="fas fa-question-circle"></i>
                Help Center
              </a>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserDashboard;
