import React, { useState } from 'react';
import { Container, Row, Col, Card, Tabs, Tab, Form, Button } from 'react-bootstrap';
import ReportList from '../reports/ReportList';

const MaintainerDashboard = () => {
  const [key, setKey] = useState('pending');
  const [filter, setFilter] = useState({
    status: 'pending'
  });

  const handleFilterChange = (e) => {
    const { name, value } = e.target;
    setFilter({ ...filter, [name]: value });
  };

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1>Maintainer Dashboard</h1>
          <p className="text-muted">Verify and assign reports to the appropriate departments</p>
          <div className="d-flex gap-2 mt-3">
            <Button variant="primary" onClick={() => window.location.reload()}>
              Refresh Reports
            </Button>
          </div>
        </Col>
      </Row>

      <Row>
        <Col>
          <Card>
            <Card.Header as="h5">Reports</Card.Header>
            <Card.Body>


              <Tabs
                id="report-tabs"
                activeKey={key}
                onSelect={(k) => {
                  setKey(k);
                  setFilter({ ...filter, status: k });
                }}
                className="mb-3"
              >
                <Tab eventKey="pending" title="Pending Reports">
                  <ReportList status="pending" />
                </Tab>
                <Tab eventKey="verified" title="Verified Reports">
                  <ReportList status="verified" />
                </Tab>
                <Tab eventKey="assigned" title="Assigned Reports">
                  <ReportList status="assigned" />
                </Tab>
                <Tab eventKey="in_progress" title="In Progress">
                  <ReportList status="in_progress" />
                </Tab>
                <Tab eventKey="resolved" title="Resolved">
                  <ReportList status="resolved" />
                </Tab>
                <Tab eventKey="fake" title="Fake Reports">
                  <ReportList status="fake" />
                </Tab>
              </Tabs>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default MaintainerDashboard;
