import React, { useState, useEffect, useMemo } from 'react';
import { Card, ListGroup, Badge, Button, Alert, Spinner } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';

const ReportList = ({ userId, status, category, view }) => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const { user } = useAuth();
  const navigate = useNavigate();

  // Handle multiple statuses separated by comma
  const statusArray = useMemo(() => status ? status.split(',') : [], [status]);

  useEffect(() => {
    const fetchReports = async () => {
      try {
        let url = '/reports';
        const params = new URLSearchParams();

        if (userId) {
          params.append('userId', userId);
        }

        if (statusArray.length > 0) {
          statusArray.forEach(s => params.append('status', s));
        }

        if (category) {
          params.append('category', category);
        }

        if (view) {
          params.append('view', view);
        }

        const response = await api.get(`${url}?${params.toString()}`);
        setReports(response.data.reports);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to fetch reports');
      } finally {
        setLoading(false);
      }
    };

    fetchReports();
  }, [userId, status, category, view]);

  const getStatusBadge = (status) => {
    switch (status) {
      case 'pending':
        return <Badge bg="info">Pending</Badge>;
      case 'verified':
        return <Badge bg="primary">Verified</Badge>;
      case 'assigned':
        return <Badge bg="warning">Assigned</Badge>;
      case 'in_progress':
        return <Badge bg="secondary">In Progress</Badge>;
      case 'resolved':
        return <Badge bg="success">Resolved</Badge>;
      case 'fake':
        return <Badge bg="danger">Fake</Badge>;
      default:
        return <Badge bg="secondary">{status}</Badge>;
    }
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'electric':
        return 'fas fa-bolt';
      case 'water':
        return 'fas fa-tint';
      case 'road':
        return 'fas fa-road';
      case 'building':
        return 'fas fa-building';
      default:
        return 'fas fa-exclamation-triangle';
    }
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString();
  };

  const handleVerify = async (reportId, status, department) => {
    try {
      const response = await api.put(`/reports/${reportId}/verify`, {
        status,
        maintainerId: user._id,
        maintainerName: user.name,
        department
      });

      // Update report in local state
      setReports(prev => 
        prev.map(report => 
          report._id === reportId ? { ...report, status } : report
        )
      );

      // Show success message
      setError('Report status updated successfully');
      setTimeout(() => setError(''), 3000);
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to update report');
    }
  };

  const handleStatusUpdate = async (reportId, newStatus) => {
    try {
      const response = await api.put(`/reports/${reportId}/status`, { status: newStatus });

      // Update report in local state
      setReports(prev =>
        prev.map(report =>
          report._id === reportId ? { ...report, status: newStatus } : report
        )
      );

      // Show success message
      setError('Report status updated successfully');
      setTimeout(() => setError(''), 3000);
    } catch (error) {
      setError(error.response?.data?.error || 'Failed to update report');
    }
  };

  const handleDelete = async (reportId) => {
    if (window.confirm('Are you sure you want to delete this report?')) {
      try {
        await api.delete(`/reports/${reportId}`);

        // Remove from local state
        setReports(prev => prev.filter(report => report._id !== reportId));

        setError('Report deleted successfully');
        setTimeout(() => setError(''), 3000);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to delete report');
      }
    }
  };

  return (
    <div className="reports-container">
      <div className="section-header mb-4">
        <h2 className="section-title">All Reports</h2>
        <div className="filter-controls d-flex gap-3 align-items-center">
          <div className="filter-group">
            <label htmlFor="status-filter" className="filter-label">Status:</label>
            <select
              id="status-filter"
              className="filter-select"
              value={status || ''}
              onChange={(e) => {
                const newStatus = e.target.value;
                const params = new URLSearchParams(window.location.search);
                if (newStatus) {
                  params.set('status', newStatus);
                } else {
                  params.delete('status');
                }
                navigate(`${window.location.pathname}?${params.toString()}`);
              }}
            >
              <option value="">All</option>
              <option value="pending">Pending</option>
              <option value="verified">Verified</option>
              <option value="assigned">Assigned</option>
              <option value="in_progress">In Progress</option>
              <option value="resolved">Resolved</option>
              <option value="fake">Fake</option>
            </select>
          </div>
          <div className="filter-group">
            <label htmlFor="category-filter" className="filter-label">Category:</label>
            <select
              id="category-filter"
              className="filter-select"
              value={category || ''}
              onChange={(e) => {
                const newCategory = e.target.value;
                const params = new URLSearchParams(window.location.search);
                if (newCategory) {
                  params.set('category', newCategory);
                } else {
                  params.delete('category');
                }
                navigate(`${window.location.pathname}?${params.toString()}`);
              }}
            >
              <option value="">All Categories</option>
              <option value="electric">Electric</option>
              <option value="water">Water</option>
              <option value="road">Road</option>
              <option value="building">Building</option>
              <option value="other">Other</option>
            </select>
          </div>
          <div className="filter-group">
            <label htmlFor="view-filter" className="filter-label">View:</label>
            <select
              id="view-filter"
              className="filter-select"
              value={view || ''}
              onChange={(e) => {
                const newView = e.target.value;
                const params = new URLSearchParams(window.location.search);
                if (newView) {
                  params.set('view', newView);
                } else {
                  params.delete('view');
                }
                navigate(`${window.location.pathname}?${params.toString()}`);
              }}
            >
              <option value="">All Views</option>
              <option value="list">List View</option>
              <option value="grid">Grid View</option>
              <option value="card">Card View</option>
            </select>
          </div>
          <Button 
            variant="outline-primary" 
            className="add-report-btn"
            as={Link}
            to="/report-form"
          >
            <i className="fas fa-plus-circle me-2"></i>
            Add New Report
          </Button>
        </div>
      </div>

      {/* Error/Success Messages */}
      {error && (
        <Alert variant="danger" className="alert-dismissible fade show" role="alert">
          <div className="d-flex align-items-center">
            <i className="fas fa-exclamation-circle me-2"></i>
            <div>{error}</div>
            <button type="button" className="btn-close" data-bs-dismiss="alert" aria-label="Close"></button>
          </div>
        </Alert>
      )}

      {/* Loading State */}
      {loading && (
        <div className="text-center py-5">
          <Spinner animation="border" variant="primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </Spinner>
        </div>
      )}

      {/* Reports List */}
      {!loading && reports.length === 0 && (
        <div className="text-center py-5">
          <i className="fas fa-inbox fa-3x text-muted mb-3"></i>
          <h3>No reports found</h3>
          <p className="text-muted">
            {status || category ? 
              `No ${status || 'all'} reports found${category ? ` in ${category} category` : ''}` : ''}
          </p>
          <Button 
            variant="primary" 
            as={Link}
            to="/report-form"
            className="mt-3"
          >
            <i className="fas fa-plus-circle me-2"></i>
            Create New Report
          </Button>
        </div>
      )}

      {/* Reports Display */}
      {!loading && reports.length > 0 && (
        <div className={view === 'card' ? 'reports-card-view' : 'reports-list-view'}>
          {reports.map(report => (
            <Card key={report._id} className="report-card">
              <Card.Header className="d-flex justify-content-between align-items-center py-3">
                <div className="d-flex align-items-center">
                  <div className="category-icon me-3">
                    <i className={getCategoryIcon(report.category)}></i>
                  </div>
                  <div>
                    <Card.Title className="mb-0">{report.title}</Card.Title>
                    <Card.Subtitle className="text-muted">
                      {report.category} â€¢ {report.location}
                    </Card.Subtitle>
                  </div>
                </div>
                <div className="d-flex align-items-center">
                  {getStatusBadge(report.status)}
                  <div className="report-actions">
                    {report.status === 'pending' && (
                      <Button 
                        variant="outline-success"
                        size="sm"
                        className="action-btn"
                        onClick={() => handleStatusUpdate(report._id, 'verified')}
                      >
                        <i className="fas fa-check me-1"></i>
                        Verify
                      </Button>
                    )}

                    {report.status === 'verified' && (
                      <Button 
                        variant="outline-primary"
                        size="sm"
                        className="action-btn"
                        onClick={() => handleStatusUpdate(report._id, 'assigned')}
                      >
                        <i className="fas fa-user-check me-1"></i>
                        Assign
                      </Button>
                    )}

                    {(report.status === 'assigned' || report.status === 'in_progress') && (
                      <Button
                        variant="outline-warning"
                        size="sm"
                        className="action-btn"
                        onClick={() => {
                          if (report.status === 'assigned') {
                            handleStatusUpdate(report._id, 'in_progress');
                          } else {
                            handleStatusUpdate(report._id, 'resolved');
                          }
                        }}
                      >
                        {report.status === 'assigned' ? (
                          <>
                            <i className="fas fa-user-check me-1"></i>
                            Start Work
                          </>
                        ) : (
                          <>
                            <i className="fas fa-check-circle me-1"></i>
                            Complete
                          </>
                        )}
                      </Button>
                    )}

                    {report.status === 'resolved' && (
                      <Button
                        variant="outline-success"
                        size="sm"
                        className="action-btn"
                        onClick={() => handleStatusUpdate(report._id, 'closed')}
                      >
                        <i className="fas fa-check-circle me-1"></i>
                        Close
                      </Button>
                    )}

                    {report.status === 'fake' && (
                      <Button 
                        variant="outline-danger"
                        size="sm"
                        className="action-btn"
                        onClick={() => handleDelete(report._id)}
                      >
                        <i className="fas fa-trash me-1"></i>
                        Delete
                      </Button>
                    )}
                  </div>
                </div>
              </Card.Header>
              <Card.Body>
                <Card.Text>
                  {report.description.length > 100 ? 
                    `${report.description.substring(0, 100)}...` 
                    : report.description
                  }
                </Card.Text>
                <div className="d-flex justify-content-between align-items-center mt-3">
                  <small className="text-muted">
                    <i className="far fa-calendar me-1"></i>
                    {formatDate(report.createdAt)}
                  </small>
                  <small className="text-muted ms-3">
                    <i className="fas fa-user me-1"></i>
                    {report.reporterName}
                  </small>
                </div>
                <div className="d-flex gap-2">
                  <small className="text-muted">
                    <i className="fas fa-hashtag me-1"></i>
                    Report ID: {report._id}
                  </small>
                  <small className="text-muted">
                    <i className="fas fa-building me-1"></i>
                    {report.department}
                  </small>
                </div>
                <div className="mt-3">
                  <Button
                    variant="outline-primary"
                    size="sm"
                    className="view-details-btn"
                    as={Link}
                    to={`/reports/${report._id}`}
                  >
                    <i className="fas fa-eye me-1"></i>
                    View Details
                  </Button>
                </div>
              </Card.Body>
            </Card>
          ))}
        </div>
      )}
    </div>
  );
};

export default ReportList;
