import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './GlassEffect.css';

const Login = () => {
  const [collegeId, setCollegeId] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (submitted || loading) return;

    setError('');
    setMessage('');
    setLoading(true);
    setSubmitted(true);

    try {
      const response = await api.post('/auth/login', { collegeId, password, role });
      login(response.data.token, response.data.user);
      setMessage('Login successful');
      setLoading(false);
      // Redirect will be handled by the AuthContext
    } catch (error) {
      const status = error.response?.status;
      if (status === 404 || status === 401) {
        setError(error.response?.data?.error || 'Invalid credentials');
        setSubmitted(false);
      } else {
        setError(error.response?.data?.error || 'Failed to login');
        setSubmitted(false);
      }
      setLoading(false);
    }
  };

  const handleInputChange = (setter) => (e) => {
    setter(e.target.value);
    if (error) setError('');
  };

  return (
    <div className="page-container">
      {/* Background Elements */}
      <div className="background-circle circle-1"></div>
      <div className="background-circle circle-2"></div>
      <div className="background-circle circle-3"></div>

      {/* Navigation Bar */}
      <nav className="nav-container">
        <div className="flex items-center">
          <svg width="30" height="30" viewBox="0 0 30 30" fill="none" xmlns="http://www.w3.org/2000/svg" className="me-2">
            <path d="M15 3C18.866 3 22.182 5.568 23.547 9.125C24.912 12.682 24.043 16.722 21.382 19.382C18.722 22.043 14.682 22.912 11.125 21.547C7.568 20.182 5 16.866 5 13C5 7.477 9.477 3 15 3Z" stroke="#C9B59C" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M15 8C17.209 8 19 9.791 19 12C19 14.209 17.209 16 15 16C12.791 16 11 14.209 11 12C11 9.791 12.791 8 15 8Z" fill="#D9CFC7"/>
            <path d="M15 10C16.105 10 17 10.895 17 12C17 13.105 16.105 14 15 14C13.895 14 13 13.105 13 12C13 10.895 13.895 10 15 10Z" fill="#F9F8F6"/>
            <path d="M10 20C10 20 12 22 15 22C18 22 20 20 20 20" stroke="#C9B59C" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          <span className="text-lg fw-medium" style={{ color: '#C9B59C' }}>Campus Issue Reporter</span>
        </div>
        <div className="d-flex align-items-center gap-3">
          <Link to="/login" className="px-3 py-2 rounded text-sm fw-medium text-decoration-none transition-all" style={{ color: '#C9B59C' }}>
            <i className="fas fa-sign-in-alt me-1"></i> Login
          </Link>
          <Link to="/register" className="px-4 py-2 text-sm fw-medium text-decoration-none transition-all rounded-pill" style={{ backgroundColor: '#C9B59C', color: '#F9F8F6' }}>
            <i className="fas fa-user-plus me-1"></i> Register
          </Link>
        </div>
      </nav>

      {/* Main Content */}
      <div className="main-content flex-column flex-md-row d-flex">
        {/* Left Side - SVG Icon and Info */}
        <div className="col-md-6 d-flex align-items-center justify-content-center p-4 p-md-5">
          <div className="text-center max-w-md">
            <div className="mb-4 float-animation">
              <svg width="280" height="280" viewBox="0 0 280 280" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M140 30C165.391 30 189.978 38.9526 209.013 55.4864C228.047 72.0202 240.724 94.7321 244.595 119.833C248.466 144.934 243.281 170.617 230.018 192.312C216.755 214.007 196.331 230.355 172.336 238.637C148.34 246.919 122.257 246.642 98.4398 237.851C74.6225 229.06 54.5484 212.285 41.7533 190.312C28.9582 168.339 24.3175 142.564 28.7383 117.562C33.1591 92.5603 46.3352 70.1337 65.7186 54.0204C85.102 37.9071 109.889 29.4815 135.277 30H140Z" stroke="#C9B59C" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round" strokeDasharray="5 5"/>
                <path d="M140 70C155.913 70 171.174 76.3214 182.426 87.5736C193.679 98.8258 200 114.087 200 130C200 145.913 193.679 161.174 182.426 172.426C171.174 183.679 155.913 190 140 190C124.087 190 108.826 183.679 97.5736 172.426C86.3214 161.174 80 145.913 80 130C80 114.087 86.3214 98.8258 97.5736 87.5736C108.826 76.3214 124.087 70 140 70Z" stroke="#D9CFC7" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M140 100C148.837 100 157.319 103.68 163.319 109.181C169.32 114.681 173 122.663 173 131C173 139.337 169.32 147.319 163.319 152.819C157.319 158.32 148.837 162 140 162C131.163 162 122.681 158.32 116.681 152.819C110.68 147.319 107 139.337 107 131C107 122.663 110.68 114.681 116.681 109.181C122.681 103.68 131.163 100 140 100Z" stroke="#EFE9E3" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M140 120C144.418 120 148.647 121.68 151.734 124.667C154.821 127.653 156.556 131.749 156.556 136C156.556 140.251 154.821 144.347 151.734 147.333C148.647 150.32 144.418 152 140 152C135.582 152 131.353 150.32 128.266 147.333C125.179 144.347 123.444 140.251 123.444 136C123.444 131.749 125.179 127.653 128.266 124.667C131.353 121.68 135.582 120 140 120Z" fill="#D9CFC7"/>
                <path d="M140 125C142.209 125 144.328 125.842 145.893 127.327C147.458 128.812 148.345 130.822 148.345 132.917C148.345 135.011 147.458 137.021 145.893 138.506C144.328 139.991 142.209 140.833 140 140.833C137.791 140.833 135.672 139.991 134.107 138.506C132.542 137.021 131.655 135.011 131.655 132.917C131.655 130.822 132.542 128.812 134.107 127.327C135.672 125.842 137.791 125 140 125Z" fill="#F9F8F6"/>
                <path d="M90 200C90 200 110 220 140 220C170 220 190 200 190 200" stroke="#C9B59C" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M110 210V230" stroke="#D9CFC7" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M170 210V230" stroke="#D9CFC7" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <h1 className="display-5 fw-bold mb-3" style={{ color: '#C9B59C' }}>Campus Issue Reporter</h1>
            <p className="fs-5 mb-4" style={{ color: '#D9CFC7' }}>Help improve your campus experience</p>
            <div className="gradient-line"></div>
            <div className="d-flex justify-content-center gap-4 mt-4">
              <div className="text-center">
                <div className="feature-icon" style={{ backgroundColor: '#EFE9E3' }}>
                  <i className="fas fa-bug" style={{ color: '#C9B59C' }}></i>
                </div>
                <p className="small mb-0" style={{ color: '#C9B59C' }}>Report Issues</p>
              </div>
              <div className="text-center">
                <div className="feature-icon" style={{ backgroundColor: '#EFE9E3' }}>
                  <i className="fas fa-chart-line" style={{ color: '#C9B59C' }}></i>
                </div>
                <p className="small mb-0" style={{ color: '#C9B59C' }}>Track Progress</p>
              </div>
              <div className="text-center">
                <div className="feature-icon" style={{ backgroundColor: '#EFE9E3' }}>
                  <i className="fas fa-comments" style={{ color: '#C9B59C' }}></i>
                </div>
                <p className="small mb-0" style={{ color: '#C9B59C' }}>Get Updates</p>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Login Form */}
        <div className="col-md-6 d-flex align-items-center justify-content-center p-4 p-md-5">
          <div className="form-container">
            {/* Glass Effect Login Form */}
            <div className="p-4 p-md-5 glass-effect rounded-4 shadow">
              <div className="text-center mb-4">
                <div className="icon-circle" style={{ backgroundColor: '#C9B59C' }}>
                  <i className="fas fa-clipboard-list text-white fs-4"></i>
                </div>
                <h2 className="fs-2 fw-bold" style={{ color: '#C9B59C' }}>Sign In</h2>
              </div>

              <form onSubmit={handleSubmit} className="space-y-4">
                {/* Error/Message Display */}
                {error && (
                  <div className={`p-3 rounded text-sm text-white ${error ? 'shake' : ''}`} style={{ backgroundColor: 'rgba(231, 76, 60, 0.8)' }}>
                    <i className="fas fa-exclamation-circle me-2"></i>
                    {error}
                  </div>
                )}
                {message && (
                  <div className="p-3 rounded text-sm" style={{ backgroundColor: 'rgba(201, 181, 156, 0.8)', color: '#F9F8F6' }}>
                    <i className="fas fa-check-circle me-2"></i>
                    {message}
                  </div>
                )}

                {/* College ID Input */}
                <div className="mb-3">
                  <label htmlFor="collegeId" className="d-block small fw-medium mb-1" style={{ color: '#C9B59C' }}>
                    <i className="fas fa-id-card me-1"></i> College ID
                  </label>
                  <div className="position-relative">
                    <input
                      id="collegeId"
                      type="text"
                      value={collegeId}
                      onChange={handleInputChange(setCollegeId)}
                      required
                      className={`form-control input-glass rounded-3 ${error ? 'is-invalid' : ''}`}
                      style={{ color: '#C9B59C' }}
                      placeholder="Enter your college ID"
                    />
                    <div className="position-absolute top-50 end-0 translate-middle-y pe-3">
                      <i className="fas fa-id-badge" style={{ color: '#D9CFC7' }}></i>
                    </div>
                  </div>
                </div>

                {/* Password Input */}
                <div className="mb-3">
                  <label htmlFor="password" className="d-block small fw-medium mb-1" style={{ color: '#C9B59C' }}>
                    <i className="fas fa-lock me-1"></i> Password
                  </label>
                  <div className="position-relative">
                    <input
                      id="password"
                      type={showPassword ? 'text' : 'password'}
                      value={password}
                      onChange={handleInputChange(setPassword)}
                      required
                      className={`form-control input-glass rounded-3 pe-5 ${error ? 'is-invalid' : ''}`}
                      style={{ color: '#C9B59C' }}
                      placeholder="Enter your password"
                    />
                    <button
                      type="button"
                      onClick={() => setShowPassword(!showPassword)}
                      className="position-absolute top-50 end-0 translate-middle-y pe-3 btn btn-link text-decoration-none p-0"
                    >
                      <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`} style={{ color: '#D9CFC7' }}></i>
                    </button>
                  </div>
                </div>

                {/* Login As Dropdown */}
                <div className="mb-3">
                  <label htmlFor="role" className="d-block small fw-medium mb-1" style={{ color: '#C9B59C' }}>
                    <i className="fas fa-user-tag me-1"></i> Login As
                  </label>
                  <div className="position-relative">
                    <select
                      id="role"
                      value={role}
                      onChange={handleInputChange(setRole)}
                      className="form-select input-glass rounded-3 custom-select"
                      style={{ color: '#C9B59C' }}
                    >
                      <option value="user">User</option>
                      <option value="maintainer">Maintainer</option>
                      <option value="field_head">Field Head</option>
                    </select>
                  </div>
                </div>

                {/* Remember Me & Forgot Password */}
                <div className="d-flex justify-content-between align-items-center mb-3">
                  <div className="form-check">
                    <input
                      id="remember-me"
                      name="remember-me"
                      type="checkbox"
                      className="form-check-input"
                      style={{ accentColor: '#C9B59C' }}
                    />
                    <label htmlFor="remember-me" className="form-check-label small ms-1" style={{ color: '#C9B59C' }}>
                      Remember me
                    </label>
                  </div>
                  <div className="small">
                    <a href="#" className="fw-medium text-decoration-none" style={{ color: '#C9B59C' }}>
                      Forgot password?
                    </a>
                  </div>
                </div>

                {/* Login Button */}
                <div>
                  <button
                    type="submit"
                    disabled={loading}
                    className="w-full d-flex justify-content-center py-3 px-4 btn text-white fw-medium rounded-3 button-glass"
                  >
                    {loading ? (
                      <>
                        <i className="fas fa-spinner fa-spin me-2"></i>
                        Signing in...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-sign-in-alt me-2"></i>
                        Sign in
                      </>
                    )}
                  </button>
                </div>
              </form>

              {/* Register Link */}
              <div className="text-center mt-4">
                <p className="small" style={{ color: '#C9B59C' }}>
