import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';

const Login = () => {
  const [collegeId, setCollegeId] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (submitted || loading) return;

    setError('');
    setMessage('');
    setLoading(true);
    setSubmitted(true);

    try {
      const response = await api.post('/auth/login', { collegeId, password, role });
      login(response.data.token, response.data.user);
      setMessage('Login successful');
      setLoading(false);
    } catch (error) {
      const status = error.response?.status;
      if (status === 404 || status === 401) {
        setError(error.response?.data?.error || 'Invalid credentials');
        setSubmitted(false);
      } else {
        setError(error.response?.data?.error || 'Failed to login');
        setSubmitted(false);
      }
      setLoading(false);
    }
  };

  const handleInputChange = (setter) => (e) => {
    setter(e.target.value);
    if (error) setError('');
  };

  return (
    <div className="relative min-h-screen bg-gradient-to-br from-[#faf4ec] via-[#f3e5d1] to-[#e8d0b1] text-[#4e3a28]">
      <div className="pointer-events-none absolute inset-0 overflow-hidden">
        <div className="absolute -top-20 -left-24 h-72 w-72 rounded-full bg-white/20 blur-3xl" />
        <div className="absolute top-32 right-10 h-56 w-56 rounded-full bg-[#cfa87a]/40 blur-2xl" />
        <div className="absolute bottom-[-120px] right-[-80px] h-80 w-80 rounded-full bg-[#d8b48c]/30 blur-3xl" />
      </div>

      <div className="relative flex min-h-screen flex-col">
        <nav className="flex items-center justify-between px-6 py-6 md:px-12">
          <div className="flex items-center gap-3">
            <div className="flex h-12 w-12 items-center justify-center rounded-2xl bg-white/70 shadow-lg shadow-[#cfa87a]/30 backdrop-blur"><span className="text-2xl text-[#b0763c]">âš‘</span></div>
            <span className="text-lg font-semibold tracking-wide text-[#7d5a35] md:text-xl">Campus Issue Reporter</span>
          </div>
          <div className="flex items-center gap-3 text-sm font-medium text-[#7d5a35]">
            <Link to="/login" className="rounded-full px-5 py-2 transition-all duration-200 hover:bg-white/60 hover:text-[#4e3a28]">Login</Link>
            <Link to="/register" className="rounded-full bg-[#b0763c] px-5 py-2 text-white shadow-lg shadow-[#b0763c]/40 transition duration-200 hover:-translate-y-0.5 hover:shadow-xl hover:shadow-[#b0763c]/40">Register</Link>
          </div>
        </nav>

        <main className="flex flex-1 flex-col items-center justify-center px-6 pb-16 pt-10 md:px-12 lg:flex-row lg:items-stretch lg:gap-16 lg:pb-24 lg:pt-0">
          <section className="flex w-full max-w-xl flex-col justify-center gap-6 text-center lg:w-1/2 lg:text-left">
            <h1 className="text-3xl font-bold leading-snug text-[#5b4026] sm:text-4xl">Streamline campus issue reporting with a refreshed experience</h1>
            <p className="text-base text-[#7f664a] sm:text-lg">Stay informed, collaborate with maintainers, and resolve challenges faster through an intuitive dashboard built for every role on campus.</p>
            <div className="grid grid-cols-1 gap-4 sm:grid-cols-3">
              <div className="rounded-2xl border border-white/40 bg-white/60 p-4 backdrop-blur">
                <div className="text-2xl font-semibold text-[#b0763c]">24/7</div>
                <p className="mt-1 text-sm text-[#7f664a]">Report anytime from any device</p>
              </div>
              <div className="rounded-2xl border border-white/40 bg-white/60 p-4 backdrop-blur">
                <div className="text-2xl font-semibold text-[#b0763c]">Live</div>
                <p className="mt-1 text-sm text-[#7f664a]">Track issue progress in real time</p>
              </div>
              <div className="rounded-2xl border border-white/40 bg-white/60 p-4 backdrop-blur">
                <div className="text-2xl font-semibold text-[#b0763c]">Secure</div>
                <p className="mt-1 text-sm text-[#7f664a]">Your data protected with care</p>
              </div>
            </div>
          </section>

          <section className="mt-12 w-full max-w-md lg:mt-0 lg:w-2/5">
            <div className="rounded-3xl border border-white/50 bg-white/70 p-8 shadow-2xl shadow-[#cfa87a]/30 backdrop-blur-xl sm:p-10">
              <div className="mb-6 text-center">
                <div className="mx-auto flex h-16 w-16 items-center justify-center rounded-2xl bg-gradient-to-br from-[#b0763c] via-[#c58a47] to-[#b0763c] text-white shadow-lg shadow-[#b0763c]/40">
                  <i className="fas fa-clipboard-list text-2xl" />
                </div>
                <h2 className="mt-5 text-2xl font-bold text-[#5b4026]">Welcome back</h2>
                <p className="mt-2 text-sm text-[#887055]">Enter your details to access your dashboard</p>
              </div>

              <form onSubmit={handleSubmit} className="space-y-5">
                {error && (
                  <div className="flex items-center gap-2 rounded-2xl bg-red-500/90 px-4 py-3 text-sm font-medium text-white shadow-lg shadow-red-500/30">
                    <i className="fas fa-exclamation-circle" />
                    <span>{error}</span>
                  </div>
                )}
                {message && (
                  <div className="flex items-center gap-2 rounded-2xl bg-[#cfa87a]/90 px-4 py-3 text-sm font-medium text-white shadow-lg shadow-[#cfa87a]/30">
                    <i className="fas fa-check-circle" />
                    <span>{message}</span>
                  </div>
                )}

                <div className="space-y-2">
                  <label htmlFor="collegeId" className="text-sm font-medium text-[#7d5a35]">College ID</label>
                  <div className="relative">
                    <input
                      id="collegeId"
                      type="text"
                      value={collegeId}
                      onChange={handleInputChange(setCollegeId)}
                      required
                      placeholder="Enter your college ID"
                      className={`w-full rounded-2xl border border-white/40 bg-white/80 px-4 py-3 pr-10 text-sm text-[#5b4026] placeholder:text-[#a78564] shadow-inner focus:border-transparent focus:outline-none focus:ring-2 focus:ring-[#c58a47] ${error ? 'ring-2 ring-red-500' : ''}`}
                    />
                    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center pr-3 text-[#c58a47]">
                      <i className="fas fa-id-badge" />
                    </div>
                  </div>
                </div>

                <div className="space-y-2">
                  <label htmlFor="password" className="text-sm font-medium text-[#7d5a35]">Password</label>
                  <div className="relative">
                    <input
                      id="password"
                      type={showPassword ? 'text' : 'password'}
                      value={password}
                      onChange={handleInputChange(setPassword)}
                      required
                      placeholder="Enter your password"
                      className={`w-full rounded-2xl border border-white/40 bg-white/80 px-4 py-3 pr-12 text-sm text-[#5b4026] placeholder:text-[#a78564] shadow-inner focus:border-transparent focus:outline-none focus:ring-2 focus:ring-[#c58a47] ${error ? 'ring-2 ring-red-500' : ''}`}
                    />
                    <button
                      type="button"
                      onClick={() => setShowPassword(!showPassword)}
                      className="absolute inset-y-0 right-0 flex items-center pr-3 text-[#c58a47] transition hover:text-[#a56732]"
                    >
                      <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`} />
                    </button>
                  </div>
                </div>

                <div className="space-y-2">
                  <label htmlFor="role" className="text-sm font-medium text-[#7d5a35]">Login as</label>
                  <div className="relative">
                    <select
                      id="role"
                      value={role}
                      onChange={handleInputChange(setRole)}
                      className="w-full appearance-none rounded-2xl border border-white/40 bg-white/80 px-4 py-3 text-sm text-[#5b4026] shadow-inner focus:border-transparent focus:outline-none focus:ring-2 focus:ring-[#c58a47]"
                    >
                      <option value="user">User</option>
                      <option value="maintainer">Maintainer</option>
                      <option value="field_head">Field Head</option>
                    </select>
                    <div className="pointer-events-none absolute inset-y-0 right-0 flex items-center px-3 text-[#c58a47]">
                      <i className="fas fa-chevron-down" />
                    </div>
                  </div>
                </div>

                <div className="flex flex-col items-start justify-between gap-3 sm:flex-row sm:items-center">
                  <label className="flex items-center gap-2 text-sm text-[#7d5a35]">
                    <input
                      id="remember-me"
                      name="remember-me"
                      type="checkbox"
                      className="h-4 w-4 rounded border-white/40 text-[#c58a47] focus:ring-[#c58a47]"
                      style={{ accentColor: '#c58a47' }}
                    />
                    Remember me
                  </label>
                  <a href="#" className="text-sm font-medium text-[#b0763c] transition hover:text-[#8a5826]">Forgot password?</a>
                </div>

                <button
                  type="submit"
                  disabled={loading}
                  className="flex w-full items-center justify-center gap-2 rounded-2xl bg-gradient-to-r from-[#b0763c] via-[#c58a47] to-[#b0763c] px-4 py-3 text-sm font-semibold text-white shadow-[0_18px_38px_-18px_rgba(181,118,60,0.9)] transition-all duration-200 hover:-translate-y-1 hover:shadow-[0_22px_44px_-18px_rgba(181,118,60,1)] focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-[#c58a47] disabled:pointer-events-none disabled:opacity-70"
                >
                  {loading ? (
                    <>
                      <i className="fas fa-spinner fa-spin" />
                      Signing in...
                    </>
                  ) : (
                    <>
                      <i className="fas fa-sign-in-alt" />
                      Sign in
                    </>
                  )}
                </button>
              </form>

              <div className="mt-8 text-center text-sm text-[#7f664a]">
                Need an account?{' '}
                <Link to="/register" className="font-semibold text-[#b0763c] transition hover:text-[#8a5826]">Register</Link>
              </div>

              <p className="mt-6 text-center text-xs text-[#a18567]">
                By signing in, you agree to our{' '}
                <a href="#" className="font-medium text-[#b0763c] underline-offset-2 hover:underline">Terms</a>{' '}
                and{' '}
                <a href="#" className="font-medium text-[#b0763c] underline-offset-2 hover:underline">Privacy Policy</a>
              </p>
            </div>
          </section>
        </main>

        <footer className="px-6 pb-10 text-sm text-[#7f664a] md:px-12">
          <div className="flex flex-col items-center justify-between gap-4 rounded-3xl border border-white/40 bg-white/50 px-6 py-5 text-center shadow-lg shadow-[#cfa87a]/20 backdrop-blur md:flex-row md:text-left">
            <p>&copy; {new Date().getFullYear()} Campus Issue Reporter. All rights reserved.</p>
            <div className="flex items-center gap-4 text-lg text-[#b0763c]">
              <a href="#" className="transition hover:text-[#8a5826]"><i className="fab fa-twitter" /></a>
              <a href="#" className="transition hover:text-[#8a5826]"><i className="fab fa-instagram" /></a>
              <a href="#" className="transition hover:text-[#8a5826]"><i className="fab fa-linkedin" /></a>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default Login;
