import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './GlassEffect.css';

const Login = () => {
  const [collegeId, setCollegeId] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (submitted || loading) return;

    setError('');
    setMessage('');
    setLoading(true);
    setSubmitted(true);

    try {
      const response = await api.post('/auth/login', { collegeId, password, role });
      login(response.data.token, response.data.user);
      setMessage('Login successful');
      setLoading(false);
      // Redirect will be handled by AuthContext
    } catch (error) {
      const status = error.response?.status;
      if (status === 404 || status === 401) {
        setError(error.response?.data?.error || 'Invalid credentials');
        setSubmitted(false);
      } else {
        setError(error.response?.data?.error || 'Failed to login');
        setSubmitted(false);
      }
      setLoading(false);
    }
  };

  const handleInputChange = (setter) => (e) => {
    setter(e.target.value);
    if (error) setError('');
  };

  return (
    <div className="page-container">
      {/* Background Elements */}
      <div className="background-shape shape-1"></div>
      <div className="background-shape shape-2"></div>
      <div className="background-shape shape-3"></div>

      {/* Navigation Bar */}
      <nav className="nav-container">
        <div className="d-flex align-items-center">
          <svg width="35" height="35" viewBox="0 0 35 35" fill="none" xmlns="http://www.w3.org/2000/svg" className="me-2">
            <path d="M17.5 3.5C21.918 3.5 25.727 6.506 27.317 10.646C28.907 14.786 27.849 19.437 24.782 22.504C21.715 25.571 17.064 26.629 12.924 25.039C8.784 23.449 5.778 19.64 5.778 15.222C5.778 8.696 10.974 3.5 17.5 3.5Z" stroke="#4A70A9" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round"/>
            <path d="M17.5 9.333C20.267 9.333 22.556 11.622 22.556 14.389C22.556 17.156 20.267 19.444 17.5 19.444C14.733 19.444 12.444 17.156 12.444 14.389C12.444 11.622 14.733 9.333 17.5 9.333Z" fill="#8FABD4"/>
            <path d="M17.5 11.667C18.839 11.667 19.944 12.772 19.944 14.111C19.944 15.45 18.839 16.556 17.5 16.556C16.161 16.556 15.056 15.45 15.056 14.111C15.056 12.772 16.161 11.667 17.5 11.667Z" fill="#EFECE3"/>
            <path d="M11.667 21.667C11.667 21.667 14.389 24.389 17.5 24.389C20.611 24.389 23.333 21.667 23.333 21.667" stroke="#4A70A9" strokeWidth="2.5" strokeLinecap="round" strokeLinejoin="round"/>
          </svg>
          <span className="fs-5 fw-bold" style={{ color: '#4A70A9' }}>Campus Issue Reporter</span>
        </div>
        <div className="d-flex align-items-center gap-3">
          <Link to="/login" className="px-3 py-2 rounded-pill text-sm fw-medium text-decoration-none" style={{ color: '#4A70A9', backgroundColor: 'rgba(74, 112, 169, 0.1)' }}>
            <i className="fas fa-sign-in-alt me-1"></i> Login
          </Link>
          <Link to="/register" className="px-4 py-2 text-sm fw-medium text-decoration-none rounded-pill" style={{ backgroundColor: '#8FABD4', color: '#000000' }}>
            <i className="fas fa-user-plus me-1"></i> Register
          </Link>
        </div>
      </nav>

      {/* Main Content */}
      <div className="main-content flex-column flex-md-row d-flex">
        {/* Left Side - SVG Icon and Info */}
        <div className="col-md-6 d-flex align-items-center justify-content-center p-4 p-md-5 order-2 order-md-1">
          <div className="text-center">
            <div className="mb-4 float-animation">
              <svg width="320" height="320" viewBox="0 0 320 320" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M160 40C189.095 40 216.909 51.7893 236.748 71.6278C256.586 91.4663 268.375 119.28 268.375 148.375C268.375 177.47 256.586 205.284 236.748 225.122C216.909 244.961 189.095 256.75 160 256.75C130.905 256.75 103.091 244.961 83.2522 225.122C63.4137 205.284 51.625 177.47 51.625 148.375C51.625 119.28 63.4137 91.4663 83.2522 71.6278C103.091 51.7893 130.905 40 160 40Z" stroke="#4A70A9" strokeWidth="3.5" strokeLinecap="round" strokeLinejoin="round" strokeDasharray="7 7"/>
                <path d="M160 80C182.642 80 203.375 90.5179 217.391 104.534C231.407 118.55 241.925 139.283 241.925 161.925C241.925 184.567 231.407 205.3 217.391 219.316C203.375 233.332 182.642 243.85 160 243.85C137.358 243.85 116.625 233.332 102.609 219.316C88.5929 205.3 78.075 184.567 78.075 161.925C78.075 139.283 88.5929 118.55 102.609 104.534C116.625 90.5179 137.358 80 160 80Z" stroke="#8FABD4" strokeWidth="3.5" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M160 120C172.957 120 184.5 126.375 192.063 133.938C199.625 141.5 206 153.043 206 166C206 178.957 199.625 190.5 192.063 198.063C184.5 205.625 172.957 212 160 212C147.043 212 135.5 205.625 127.938 198.063C120.375 190.5 114 178.957 114 166C114 153.043 120.375 141.5 127.938 133.938C135.5 126.375 147.043 120 160 120Z" stroke="#EFECE3" strokeWidth="3.5" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M160 140C166.627 140 172.353 143.074 176.639 147.361C180.926 151.647 184 157.373 184 164C184 170.627 180.926 176.353 176.639 180.639C172.353 184.926 166.627 188 160 188C153.373 188 147.647 184.926 143.361 180.639C139.074 176.353 136 170.627 136 164C136 157.373 139.074 151.647 143.361 147.361C147.647 143.074 153.373 140 160 140Z" fill="#8FABD4"/>
                <path d="M160 150C163.452 150 166.565 151.485 168.79 153.71C171.015 155.935 172.5 159.048 172.5 162.5C172.5 165.952 171.015 169.065 168.79 171.29C166.565 173.515 163.452 175 160 175C156.548 175 153.435 173.515 151.21 171.29C148.985 169.065 147.5 165.952 147.5 162.5C147.5 159.048 148.985 155.935 151.21 153.71C153.435 151.485 156.548 150 160 150Z" fill="#EFECE3"/>
                <path d="M120 220C120 220 140 240 160 240C180 240 200 220 200 220" stroke="#4A70A9" strokeWidth="3.5" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M135 230V250" stroke="#8FABD4" strokeWidth="3.5" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M185 230V250" stroke="#8FABD4" strokeWidth="3.5" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <h1 className="display-4 fw-bold mb-3" style={{ color: '#4A70A9' }}>Campus Issue Reporter</h1>
            <p className="fs-5 mb-4" style={{ color: '#8FABD4' }}>Help improve your campus experience</p>
            <div className="gradient-line"></div>
            <div className="d-flex justify-content-center gap-4 mt-5">
              <div className="text-center">
                <div className="feature-icon">
                  <i className="fas fa-bug fs-4" style={{ color: '#4A70A9' }}></i>
                </div>
                <p className="small fw-medium mb-0" style={{ color: '#000000' }}>Report Issues</p>
              </div>
              <div className="text-center">
                <div className="feature-icon">
                  <i className="fas fa-chart-line fs-4" style={{ color: '#4A70A9' }}></i>
                </div>
                <p className="small fw-medium mb-0" style={{ color: '#000000' }}>Track Progress</p>
              </div>
              <div className="text-center">
                <div className="feature-icon">
                  <i className="fas fa-comments fs-4" style={{ color: '#4A70A9' }}></i>
                </div>
                <p className="small fw-medium mb-0" style={{ color: '#000000' }}>Get Updates</p>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Login Form */}
        <div className="col-md-6 d-flex align-items-center justify-content-center p-4 p-md-5 order-1 order-md-2">
          <div className="form-container">
            {/* Glass Effect Login Form */}
            <div className="p-4 p-md-5 glass-effect rounded-4">
              <div className="text-center mb-4">
                <div className="icon-circle">
                  <i className="fas fa-clipboard-list text-white fs-3"></i>
                </div>
                <h2 className="fs-2 fw-bold" style={{ color: '#4A70A9' }}>Sign In</h2>
                <p className="text-muted mt-2">Enter your credentials to access your account</p>
              </div>

              <form onSubmit={handleSubmit}>
                {/* Error/Message Display */}
                {error && (
                  <div className={`alert alert-danger d-flex align-items-center ${error ? 'shake' : ''}`} role="alert">
                    <i className="fas fa-exclamation-circle me-2"></i>
                    <div>{error}</div>
                  </div>
                )}
                {message && (
                  <div className="alert alert-success d-flex align-items-center" role="alert">
                    <i className="fas fa-check-circle me-2"></i>
                    <div>{message}</div>
                  </div>
                )}

                {/* College ID Input */}
                <div className="input-group">
                  <label htmlFor="collegeId" className="form-label">
                    <i className="fas fa-id-card me-1"></i> College ID
                  </label>
                  <div className="position-relative">
                    <input
                      id="collegeId"
                      type="text"
                      value={collegeId}
                      onChange={handleInputChange(setCollegeId)}
                      required
                      className={`form-control input-glass rounded-3 ps-3 pe-5 ${error ? 'is-invalid' : ''}`}
                      style={{ fontSize: '1rem', padding: '12px 15px' }}
                      placeholder="Enter your college ID"
                    />
                    <div className="input-icon">
                      <i className="fas fa-id-badge"></i>
                    </div>
                  </div>
                </div>

                {/* Password Input */}
                <div className="input-group">
                  <label htmlFor="password" className="form-label">
                    <i className="fas fa-lock me-1"></i> Password
                  </label>
                  <div className="position-relative">
                    <input
                      id="password"
                      type={showPassword ? 'text' : 'password'}
                      value={password}
                      onChange={handleInputChange(setPassword)}
                      required
                      className={`form-control input-glass rounded-3 ps-3 pe-5 ${error ? 'is-invalid' : ''}`}
                      style={{ fontSize: '1rem', padding: '12px 15px' }}
                      placeholder="Enter your password"
                    />
                    <button
                      type="button"
                      onClick={() => setShowPassword(!showPassword)}
                      className="btn btn-link position-absolute top-50 end-0 translate-middle-y pe-3 text-decoration-none p-0"
                      style={{ color: '#8FABD4' }}
                    >
                      <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`}></i>
                    </button>
                  </div>
                </div>

                {/* Login As Dropdown */}
                <div className="input-group">
                  <label htmlFor="role" className="form-label">
                    <i className="fas fa-user-tag me-1"></i> Login As
                  </label>
                  <div className="position-relative">
                    <select
                      id="role"
                      value={role}
                      onChange={handleInputChange(setRole)}
                      className="form-select input-glass rounded-3 custom-select"
                      style={{ fontSize: '1rem', padding: '12px 15px' }}
                    >
                      <option value="user">User</option>
                      <option value="maintainer">Maintainer</option>
                      <option value="field_head">Field Head</option>
                    </select>
                  </div>
                </div>

                {/* Remember Me & Forgot Password */}
                <div className="d-flex justify-content-between align-items-center mb-4">
                  <div className="checkbox-wrapper">
                    <input
                      id="remember-me"
                      name="remember-me"
                      type="checkbox"
                      className="checkbox-custom form-check-input"
                    />
                    <label htmlFor="remember-me" className="form-check-label ms-2" style={{ color: '#000000' }}>
                      Remember me
                    </label>
                  </div>
                  <div>
                    <a href="#" className="link-text">
                      Forgot password?
                    </a>
                  </div>
                </div>

                {/* Login Button */}
                <div className="d-grid mb-4">
                  <button
                    type="submit"
                    disabled={loading}
                    className="button-primary"
                  >
                    {loading ? (
                      <>
                        <i className="fas fa-spinner fa-spin me-2"></i>
                        Signing in...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-sign-in-alt me-2"></i>
                        Sign in
                      </>
                    )}
                  </button>
                </div>

