import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Button, Container } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Header = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 20) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <>
      {/* Glass Header */}
      <div 
        className={`glass-header ${scrolled ? 'scrolled' : ''}`}
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          zIndex: 1000,
          backgroundColor: 'rgba(128, 172, 194, 0.15)',
          backdropFilter: 'blur(10px)',
          WebkitBackdropFilter: 'blur(10px)',
          borderBottom: '1px solid rgba(255, 255, 255, 0.1)',
          transition: 'all 0.3s ease'
        }}
      >
        <Container>
          <Navbar expand="lg" className="py-3" style={{ backgroundColor: 'transparent' }}>
            <Navbar.Brand 
              as={Link} 
              to="/" 
              className="d-flex align-items-center"
              style={{ 
                color: '#d26565ff', 
                fontWeight: '600',
                fontSize: '1.25rem',
                textDecoration: 'none'
              }}
            >
              <div 
                className="d-flex align-items-center justify-content-center me-2"
                style={{
                  width: '40px',
                  height: '40px',
                  borderRadius: '50%',
                  backgroundColor: 'rgba(255, 255, 255, 0.2)',
                  backdropFilter: 'blur(5px)',
                  WebkitBackdropFilter: 'blur(5px)'
                }}
              >
                <i className="fas fa-university"></i>
              </div>
              Campus Issue Reporter
            </Navbar.Brand>
            <Navbar.Toggle 
              aria-controls="basic-navbar-nav" 
              style={{
                borderColor: 'rgba(255, 255, 255, 0.3)',
                backgroundColor: 'rgba(255, 255, 255, 0.1)'
              }}
            />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="me-auto">
                {user && (
                  <>
                    <Nav.Link 
                      as={Link} 
                      to="/reports" 
                      className="nav-link-glass"
                      style={{ color: '#ffffff', fontWeight: '500' }}
                    >
                      <i className="fas fa-clipboard-list me-2"></i>
                      Reports
                    </Nav.Link>
                    <Nav.Link 
                      as={Link} 
                      to="/report-form" 
                      className="nav-link-glass"
                      style={{ color: '#ffffff', fontWeight: '500' }}
                    >
                      <i className="fas fa-plus-circle me-2"></i>
                      New Report
                    </Nav.Link>
                    {user.role === 'user' && (
                      <Nav.Link 
                        as={Link} 
                        to="/user-dashboard" 
                        className="nav-link-glass"
                        style={{ color: '#ffffff', fontWeight: '500' }}
                      >
                        <i className="fas fa-tachometer-alt me-2"></i>
                        Dashboard
                      </Nav.Link>
                    )}
                    {user.role === 'maintainer' && (
                      <Nav.Link 
                        as={Link} 
                        to="/maintainer-dashboard" 
                        className="nav-link-glass"
                        style={{ color: '#ffffff', fontWeight: '500' }}
                      >
                        <i className="fas fa-tachometer-alt me-2"></i>
                        Dashboard
                      </Nav.Link>
                    )}
                    {user.role === 'fieldhead' && (
                      <Nav.Link 
                        as={Link} 
                        to="/fieldhead-dashboard" 
                        className="nav-link-glass"
                        style={{ color: '#ffffff', fontWeight: '500' }}
                      >
                        <i className="fas fa-tachometer-alt me-2"></i>
                        Dashboard
                      </Nav.Link>
                    )}
                    {user.role === 'field_head' && (
                      <>
                        <Nav.Link 
                          as={Link} 
                          to="/all-reports" 
                          className="nav-link-glass"
                          style={{ color: '#ffffff', fontWeight: '500' }}
                        >
                          <i className="fas fa-list-alt me-2"></i>
                          All Reports
                        </Nav.Link>
                        <Nav.Link 
                          as={Link} 
                          to="/history" 
                          className="nav-link-glass"
                          style={{ color: '#ffffff', fontWeight: '500' }}
                        >
                          <i className="fas fa-history me-2"></i>
                          History
                        </Nav.Link>
                      </>
                    )}
                  </>
                )}
              </Nav>
              <Nav>
                {user ? (
                  <div className="d-flex align-items-center">
                    <div 
                      className="d-flex align-items-center me-3"
                      style={{
                        padding: '8px 16px',
                        borderRadius: '30px',
                        backgroundColor: 'rgba(255, 255, 255, 0.1)',
                        backdropFilter: 'blur(5px)',
                        WebkitBackdropFilter: 'blur(5px)'
                      }}
                    >
                      <i className="fas fa-user me-2"></i>
                      <span style={{ color: '#ffffff', fontWeight: '500' }}>
                        {user.name}
                      </span>
                    </div>
                    <Button 
                      variant="outline-light" 
                      onClick={handleLogout}
                      className="glass-button"
                      style={{
                        borderColor: 'rgba(255, 255, 255, 0.3)',
                        color: '#ffffff',
                        fontWeight: '500',
                        borderRadius: '30px',
                        padding: '8px 20px',
                        backgroundColor: 'rgba(255, 255, 255, 0.1)',
                        backdropFilter: 'blur(5px)',
                        WebkitBackdropFilter: 'blur(5px)',
                        transition: 'all 0.3s ease'
                      }}
                      onMouseEnter={(e) => {
                        e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.2)';
                        e.target.style.borderColor = 'rgba(255, 255, 255, 0.5)';
                      }}
                      onMouseLeave={(e) => {
                        e.target.style.backgroundColor = 'rgba(255, 255, 255, 0.1)';
                        e.target.style.borderColor = 'rgba(255, 255, 255, 0.3)';
                      }}
                    >
                      <i className="fas fa-sign-out-alt me-2"></i>
                      Logout
                    </Button>
                  </div>
                ) : (
                  <>
                    <Button 
                      variant="outline-light" 
                      as={Link}
                      to="/login"
                      className="glass-button me-2"
                      style={{
                        borderColor: 'rgba(255, 255, 255, 0.3)',
                        color: '#ffffff',
                        fontWeight: '500',
                        borderRadius: '30px',
                        padding: '8px 20px',
                        backgroundColor: 'rgba(255, 255, 255, 0.1)',
                        backdropFilter: 'blur(5px)',
                        WebkitBackdropFilter: 'blur(5px)',
                        transition: 'all 0.3s ease'
                      }}
                    >
                      <i className="fas fa-sign-in-alt me-2"></i>
                      Login
                    </Button>
                    <Button 
                      variant="outline-light" 
                      as={Link}
                      to="/register"
                      className="glass-button"
                      style={{
                        borderColor: 'rgba(255, 255, 255, 0.3)',
                        color: '#ffffff',
                        fontWeight: '500',
                        borderRadius: '30px',
                        padding: '8px 20px',
                        backgroundColor: 'rgba(255, 255, 255, 0.1)',
                        backdropFilter: 'blur(5px)',
                        WebkitBackdropFilter: 'blur(5px)',
                        transition: 'all 0.3s ease'
                      }}
                    >
                      <i className="fas fa-user-plus me-2"></i>
                      Register
                    </Button>
                  </>
                )}
              </Nav>
            </Navbar.Collapse>
          </Navbar>
        </Container>
      </div>

      {/* Spacer to prevent content from hiding under fixed header */}
      <div style={{ height: '80px' }}></div>

      <style jsx>{`
        .nav-link-glass {
          position: relative;
          transition: all 0.3s ease;
          border-radius: 8px;
          padding: 8px 16px;
          margin: 0 5px;
        }

        .nav-link-glass:hover {
          background-color: rgba(255, 255, 255, 0.1);
          transform: translateY(-2px);
        }

        .nav-link-glass.active {
          background-color: rgba(255, 255, 255, 0.15);
        }

        .glass-header.scrolled {
          background-color: rgba(128, 172, 194, 0.25);
          backdrop-filter: blur(15px);
          -webkit-backdrop-filter: blur(15px);
        }

        .glass-button:hover {
          transform: translateY(-2px);
          box-shadow: 0 5px 15px rgba(0, 0, 0, 0.1);
        }

        .navbar-toggler {
          transition: all 0.3s ease;
        }

        .navbar-toggler:hover {
          background-color: rgba(255, 255, 255, 0.2) !important;
        }

        .navbar-toggler:focus {
          box-shadow: 0 0 0 0.2rem rgba(128, 172, 194, 0.25);
        }
      `}</style>
    </>
  );
};

export default Header;
