import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { Card, Badge, Button, Spinner, Alert, Row, Col } from 'react-bootstrap';
import api from '../../services/api';

const ReportList = ({ userId, filter = 'all' }) => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchReports = async () => {
      try {
        let url = '/reports';
        if (userId) {
          url += `?userId=${userId}`;
        }
        if (filter !== 'all') {
          url += userId ? `&status=${filter}` : `?status=${filter}`;
        }

        const response = await api.get(url);
        setReports(response.data);
      } catch (error) {
        console.error('Failed to fetch reports:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchReports();
  }, [userId, filter]);

  const getStatusVariant = (status) => {
    switch (status) {
      case 'pending':
        return 'warning';
      case 'in-progress':
        return 'info';
      case 'resolved':
        return 'success';
      default:
        return 'secondary';
    }
  };

  const getStatusIcon = (status) => {
    switch (status) {
      case 'pending':
        return 'fas fa-clock';
      case 'in-progress':
        return 'fas fa-spinner fa-pulse';
      case 'resolved':
        return 'fas fa-check-circle';
      default:
        return 'fas fa-question-circle';
    }
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'electric':
        return 'fas fa-bolt';
      case 'water':
        return 'fas fa-tint';
      case 'road':
        return 'fas fa-road';
      case 'building':
        return 'fas fa-building';
      default:
        return 'fas fa-exclamation-triangle';
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  if (loading) {
    return (
      <div className="d-flex justify-content-center p-5">
        <div className="text-center">
          <Spinner animation="border" variant="primary" style={{ color: '#80ACC2' }} />
          <p className="mt-3 text-muted">Loading your reports...</p>
        </div>
      </div>
    );
  }

  if (reports.length === 0) {
    return (
      <Alert variant="info" className="text-center p-5">
        <div className="mb-3">
          <i className="fas fa-clipboard-list fa-3x" style={{ color: '#80ACC2' }}></i>
        </div>
        <h4 className="mb-2">No reports found</h4>
        <p className="mb-3">
          {filter === 'all' 
            ? "You haven't submitted any reports yet. Start by reporting a new issue." 
            : `You don't have any ${filter} reports.`
          }
        </p>
        <Button 
          variant="primary" 
          className="px-4 py-2"
          style={{ backgroundColor: '#80ACC2', border: 'none' }}
          as={Link}
          to="/report-form"
        >
          <i className="fas fa-plus me-2"></i>
          Report New Issue
        </Button>
      </Alert>
    );
  }

  return (
    <Row className="g-4">
      {reports.map(report => (
        <Col md={6} lg={4} key={report._id}>
          <Card className="h-100 border-0 shadow-sm hover-card">
            <Card.Header className="bg-white border-bottom-0 pb-0">
              <div className="d-flex justify-content-between align-items-start">
                <div className="d-flex align-items-center">
                  <div 
                    className="rounded-circle d-flex align-items-center justify-content-center me-3"
                    style={{ 
                      width: '48px', 
                      height: '48px', 
                      backgroundColor: '#e7f5fd' 
                    }}
                  >
                    <i className={`${getCategoryIcon(report.category)}`} style={{ color: '#80ACC2' }}></i>
                  </div>
                  <div>
                    <h5 className="fw-bold mb-1" style={{ color: '#4a6b7a' }}>{report.title}</h5>
                    <small className="text-muted">
                      <i className="far fa-calendar me-1"></i>
                      {formatDate(report.createdAt)}
                    </small>
                  </div>
                </div>
                <Badge 
                  bg={getStatusVariant(report.status)} 
                  className="d-flex align-items-center text-capitalize px-3 py-2"
                >
                  <i className={`${getStatusIcon(report.status)} me-1`}></i>
                  {report.status.replace('-', ' ')}
                </Badge>
              </div>
            </Card.Header>
            <Card.Body className="pt-0">
              <p className="text-muted mb-3">
                {report.description.length > 120 
                  ? `${report.description.substring(0, 120)}...` 
                  : report.description
                }
              </p>
              <div className="d-flex justify-content-between align-items-center">
                <div className="d-flex gap-3">
                  <span className="text-muted small">
                    <i className="fas fa-map-marker-alt me-1"></i>
                    {report.location}
                  </span>
                  <span className="text-muted small">
                    <i className="fas fa-tag me-1"></i>
                    {report.category}
                  </span>
                </div>
                <Button 
                  variant="outline-primary" 
                  size="sm"
                  className="rounded-pill"
                  style={{ color: '#80ACC2', borderColor: '#80ACC2' }}
                  as={Link}
                  to={`/reports/${report._id}`}
                >
                  View Details
                  <i className="fas fa-arrow-right ms-1"></i>
                </Button>
              </div>
            </Card.Body>
          </Card>
        </Col>
      ))}
    </Row>
  );
};

export default ReportList;
