import React, { useState } from 'react';
import { Container, Row, Col, Card, Tabs, Tab, Button, Alert } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import ReportList from '../reports/ReportList';
import api from '../../services/api';

const FieldHeadDashboard = () => {
  const { user } = useAuth();
  const [bulkActionLoading, setBulkActionLoading] = useState(false);
  const [alertMessage, setAlertMessage] = useState('');
  const [alertType, setAlertType] = useState('');

  const handleBulkVerify = async () => {
    if (!user?.department) return;

    setBulkActionLoading(true);
    try {
      const response = await api.put('/reports/bulk-verify', {
        department: user.department,
        fieldHeadId: user._id
      });

      setAlertMessage(`Successfully verified ${response.data.verifiedCount} reports`);
      setAlertType('success');
      setTimeout(() => {
        setAlertMessage('');
        window.location.reload();
      }, 3000);
    } catch (error) {
      setAlertMessage(error.response?.data?.error || 'Failed to verify reports');
      setAlertType('danger');
    } finally {
      setBulkActionLoading(false);
    }
  };

  const handleBulkMarkFake = async () => {
    if (!user?.department) return;

    setBulkActionLoading(true);
    try {
      const response = await api.put('/reports/bulk-mark-fake', {
        department: user.department,
        fieldHeadId: user._id
      });

      setAlertMessage(`Successfully marked ${response.data.markedCount} reports as fake`);
      setAlertType('success');
      setTimeout(() => {
        setAlertMessage('');
        window.location.reload();
      }, 3000);
    } catch (error) {
      setAlertMessage(error.response?.data?.error || 'Failed to mark reports as fake');
      setAlertType('danger');
    } finally {
      setBulkActionLoading(false);
    }
  };

  return (
    <Container>
      {/* Alert Messages */}
      {alertMessage && (
        <Row className="mb-3">
          <Col>
            <Alert variant={alertType} dismissible onClose={() => setAlertMessage('')}>
              <i className={`fas fa-${alertType === 'success' ? 'check-circle' : 'exclamation-triangle'} me-2`}></i>
              {alertMessage}
            </Alert>
          </Col>
        </Row>
      )}

      <Row className="mb-4">
        <Col>
          <h1>Field Head Dashboard</h1>
          <p className="text-muted">Department: {user?.department} - Verify and manage reports in your department</p>
          <div className="d-flex gap-2 mt-3">
            <Button variant="primary" onClick={() => window.location.reload()}>
              Refresh Reports
            </Button>
            <Button as={Link} to="/history" variant="outline-secondary">
              View History
            </Button>
          </div>
        </Col>
      </Row>

      <Row className="mb-4">
        <Col>
          <Card className="shadow-sm">
            <Card.Header className="bg-primary text-white d-flex justify-content-between align-items-center">
              <h5 className="mb-0">
                <i className="fas fa-check-circle me-2"></i>
                Quick Actions
              </h5>
              <small className="text-light">Field Head Controls</small>
            </Card.Header>
            <Card.Body>
              <div className="d-flex gap-3 flex-wrap">
                <Button
                  variant="success"
                  size="lg"
                  className="d-flex align-items-center gap-2"
                  onClick={handleBulkVerify}
                  disabled={bulkActionLoading}
                >
                  {bulkActionLoading ? (
                    <i className="fas fa-spinner fa-spin"></i>
                  ) : (
                    <i className="fas fa-check-double"></i>
                  )}
                  <span>Verify All Pending</span>
                </Button>
                <Button
                  variant="danger"
                  size="lg"
                  className="d-flex align-items-center gap-2"
                  onClick={handleBulkMarkFake}
                  disabled={bulkActionLoading}
                >
                  {bulkActionLoading ? (
                    <i className="fas fa-spinner fa-spin"></i>
                  ) : (
                    <i className="fas fa-times-circle"></i>
                  )}
                  <span>Mark as Fake</span>
                </Button>
              </div>
              <p className="text-muted mt-3 mb-0">
                <i className="fas fa-info-circle me-1"></i>
                Use these buttons to quickly manage multiple reports in your department.
              </p>
            </Card.Body>
          </Card>
        </Col>
      </Row>

      <Row>
        <Col>
          <Card>
            <Card.Header as="h5">Verify Reports</Card.Header>
            <Card.Body>
              <ReportList view="verify_section" />
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default FieldHeadDashboard;
