import React, { useState } from 'react';
import { Button, Alert, Card, Container, Row, Col, Form, Spinner } from 'react-bootstrap';
import Link from 'next/link';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import { 
  ShieldCheck, 
  Users, 
  HardHat, 
  UserCheck, 
  AlertCircle, 
  CheckCircle2,
  ArrowRight,
  MapPin,
  MessageSquare,
  Wrench
} from 'lucide-react';

const Login = () => {
  const [collegeId, setCollegeId] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (submitted || loading) return; // Prevent multiple submissions

    setError('');
    setMessage('');
    setLoading(true);
    setSubmitted(true);

    try {
      const response = await api.post('/auth/login', { collegeId, password, role });
      login(response.data.token, response.data.user);
      setMessage('Login successful');
      setLoading(false);
      // Redirect will be handled by the AuthContext
    } catch (error) {
      const status = error.response?.status;
      if (status === 404 || status === 401) {
        setError(error.response?.data?.error || 'Invalid credentials');
        setSubmitted(false); // Allow retry only for auth errors
      } else {
        setError(error.response?.data?.error || 'Failed to login');
        setSubmitted(false); // Allow retry for other errors
      }
      setLoading(false);
    }
  };

  const getRoleIcon = (roleValue) => {
    switch(roleValue) {
      case 'maintainer': return <Wrench className="w-4 h-4" />;
      case 'field_head': return <ShieldCheck className="w-4 h-4" />;
      default: return <UserCheck className="w-4 h-4" />;
    }
  };

  return (
    <Container fluid className="vh-100 p-0">
      <Row className="g-0 h-100">
        {/* Left Panel - Campus Issue Reporter Info */}
        <Col lg={7} className="d-none d-lg-flex" style={{background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}>
          <div className="d-flex flex-column justify-content-center px-5 py-4">
            <div className="mb-5">
              <div className="d-flex align-items-center mb-4">
                <div className="bg-white text-white p-3 rounded-lg me-3" style={{backgroundColor: 'rgba(255,255,255,0.2)', backdropFilter: 'blur(10px)'}}>
                  <MapPin className="w-8 h-8" />
                </div>
                <div>
                  <h1 className="h2 fw-bold mb-0 text-white">Campus Issue Reporter</h1>
                  <p className="text-white-50 mb-0">Report • Track • Resolve</p>
                </div>
              </div>
              <p className="text-white fs-5 mb-4" style={{opacity: 0.9}}>
                Your comprehensive platform for reporting and tracking campus issues. 
                From maintenance requests to facility concerns, we ensure your voice is heard.
              </p>
            </div>

            <div className="row g-4 mb-5">
              <div className="col-md-6">
                <div className="bg-white rounded-lg p-4 shadow-sm h-100">
                  <div className="d-flex align-items-center mb-3">
                    <div className="bg-blue-100 p-2 rounded me-3">
                      <MessageSquare className="w-6 h-6 text-blue-600" />
                    </div>
                    <h3 className="h6 fw-semibold mb-0">Report Issues</h3>
                  </div>
                  <p className="text-muted small mb-0">
                    Quickly report campus issues with detailed descriptions and locations
                  </p>
                </div>
              </div>
              <div className="col-md-6">
                <div className="bg-white rounded-lg p-4 shadow-sm h-100">
                  <div className="d-flex align-items-center mb-3">
                    <div className="bg-green-100 p-2 rounded me-3">
                      <Users className="w-6 h-6 text-green-600" />
                    </div>
                    <h3 className="h6 fw-semibold mb-0">Multi-Role System</h3>
                  </div>
                  <p className="text-muted small mb-0">
                    Different access levels for users, maintainers, and field heads
                  </p>
                </div>
              </div>
              <div className="col-md-6">
                <div className="bg-white rounded-lg p-4 shadow-sm h-100">
                  <div className="d-flex align-items-center mb-3">
                    <div className="bg-purple-100 p-2 rounded me-3">
                      <AlertCircle className="w-6 h-6 text-purple-600" />
                    </div>
                    <h3 className="h6 fw-semibold mb-0">Real-Time Updates</h3>
                  </div>
                  <p className="text-muted small mb-0">
                    Track the status of your reported issues in real-time
                  </p>
                </div>
              </div>
              <div className="col-md-6">
                <div className="bg-white rounded-lg p-4 shadow-sm h-100">
                  <div className="d-flex align-items-center mb-3">
                    <div className="bg-orange-100 p-2 rounded me-3">
                      <Wrench className="w-6 h-6 text-orange-600" />
                    </div>
                    <h3 className="h6 fw-semibold mb-0">Quick Resolution</h3>
                  </div>
                  <p className="text-muted small mb-0">
                    Efficient assignment and resolution of maintenance tasks
                  </p>
                </div>
              </div>
            </div>

            <div className="bg-white rounded-lg p-4 shadow-sm">
              <h3 className="h6 fw-semibold mb-3">How It Works</h3>
              <div className="d-flex align-items-center mb-3">
                <div className="bg-primary text-white rounded-circle d-flex align-items-center justify-content-center me-3" style={{width: '28px', height: '28px'}}>
                  <span className="fw-bold small">1</span>
                </div>
                <p className="mb-0 text-muted">Report an issue with details and location</p>
              </div>
              <div className="d-flex align-items-center mb-3">
                <div className="bg-primary text-white rounded-circle d-flex align-items-center justify-content-center me-3" style={{width: '28px', height: '28px'}}>
                  <span className="fw-bold small">2</span>
                </div>
                <p className="mb-0 text-muted">Track status updates in real-time</p>
              </div>
              <div className="d-flex align-items-center">
                <div className="bg-primary text-white rounded-circle d-flex align-items-center justify-content-center me-3" style={{width: '28px', height: '28px'}}>
                  <span className="fw-bold small">3</span>
                </div>
                <p className="mb-0 text-muted">Get notified when issues are resolved</p>
              </div>
            </div>
          </div>
        </Col>

        {/* Right Panel - Login Form */}
        <Col lg={5} className="d-flex flex-column justify-content-center bg-white">
          <div className="px-4 py-5 px-lg-5">
            <div className="text-center mb-5">
              <div className="d-inline-flex align-items-center justify-content-center bg-primary bg-opacity-10 rounded-circle mb-4" style={{width: '72px', height: '72px'}}>
                <ShieldCheck className="w-8 h-8 text-primary" />
              </div>
              <h2 className="fw-bold mb-2">Welcome Back</h2>
              <p className="text-muted">Sign in to access your dashboard</p>
            </div>

            {error && (
              <Alert variant="danger" className="mb-4 border-0">
                <div className="d-flex align-items-center">
                  <AlertCircle className="w-5 h-5 me-2" />
                  {error}
                </div>
              </Alert>
            )}
            {message && (
              <Alert variant="success" className="mb-4 border-0">
                <div className="d-flex align-items-center">
                  <CheckCircle2 className="w-5 h-5 me-2" />
                  {message}
                </div>
              </Alert>
            )}

            <Form onSubmit={handleSubmit}>
              <Form.Group className="mb-4">
                <Form.Label className="fw-semibold">College ID</Form.Label>
                <Form.Control
                  type="text"
                  value={collegeId}
                  onChange={(e) => setCollegeId(e.target.value)}
                  required
                  placeholder="Enter your college ID"
                  className="py-3 px-4"
                  style={{borderRadius: '8px', border: '1px solid #e2e8f0'}}
                />
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Label className="fw-semibold">Password</Form.Label>
                <Form.Control
                  type="password"
                  value={password}
                  onChange={(e) => setPassword(e.target.value)}
                  required
                  placeholder="Enter your password"
                  className="py-3 px-4"
                  style={{borderRadius: '8px', border: '1px solid #e2e8f0'}}
                />
              </Form.Group>

              <Form.Group className="mb-4">
                <Form.Label className="fw-semibold">Login As</Form.Label>
                <div className="position-relative">
                  <Form.Select 
                    value={role} 
                    onChange={(e) => setRole(e.target.value)}
                    className="py-3 px-4"
                    style={{borderRadius: '8px', border: '1px solid #e2e8f0', appearance: 'none'}}
                  >
                    <option value="user">User</option>
                    <option value="maintainer">Maintainer</option>
                    <option value="field_head">Field Head</option>
                  </Form.Select>
                  <div className="position-absolute end-0 top-50 translate-middle-y pe-3 pointer-events-none">
                    {getRoleIcon(role)}
                  </div>
                </div>
              </Form.Group>

              <Button 
                disabled={loading} 
                className="w-100 py-3 border-0 fw-semibold" 
                type="submit"
                style={{borderRadius: '8px', background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)'}}
              >
                {loading ? (
                  <div className="d-flex align-items-center justify-content-center">
                    <Spinner as="span" animation="border" size="sm" role="status" aria-hidden="true" className="me-2" />
                    Signing in...
                  </div>
                ) : (
                  <div className="d-flex align-items-center justify-content-center">
                    Sign In
                    <ArrowRight className="w-4 h-4 ms-2" />
                  </div>
                )}
              </Button>
            </Form>

            <div className="text-center mt-4">
              <p className="text-muted mb-0">
                Don't have an account?{' '}
                <Link href="/register" className="text-primary text-decoration-none fw-semibold">
                  Register here
                </Link>
              </p>
            </div>
          </div>
        </Col>
      </Row>
    </Container>
  );
};

export default Login;
