import React, { useState } from 'react';
import { Container, Row, Col, Card, Tabs, Tab } from 'react-bootstrap';
import { useAuth } from '../../context/AuthContext';
import ReportList from '../reports/ReportList';

const FieldHeadDashboard = () => {
  const { user } = useAuth();
  const [key, setKey] = useState('assigned');

  return (
    <Container>
      <Row className="mb-4">
        <Col>
          <h1>Field Head Dashboard</h1>
          <p className="text-muted">Department: {user?.department}</p>
        </Col>
      </Row>

      <Row>
        <Col>
          <Card>
            <Card.Header as="h5">Reports</Card.Header>
            <Card.Body>
              <Tabs
                id="report-tabs"
                activeKey={key}
                onSelect={(k) => setKey(k)}
                className="mb-3"
              >
                <Tab eventKey="assigned" title="Assigned Reports">
                  <ReportList status="assigned" />
                </Tab>
                <Tab eventKey="in_progress" title="In Progress">
                  <ReportList status="in_progress" />
                </Tab>
                <Tab eventKey="resolved" title="Resolved">
                  <ReportList status="resolved" />
                </Tab>
              </Tabs>
            </Card.Body>
          </Card>
        </Col>
      </Row>
    </Container>
  );
};

export default FieldHeadDashboard;
