import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Card, ListGroup, Badge, Button, Alert, Spinner } from 'react-bootstrap';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';

const AllReports = () => {
  const [reports, setReports] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [filter, setFilter] = useState('all');
  const { user } = useAuth();

  useEffect(() => {
    const fetchReports = async () => {
      try {
        // Fetch all reports for the field head's department
        const response = await api.get(`/reports/department/${user.department}`);
        setReports(response.data.reports);
      } catch (error) {
        setError(error.response?.data?.error || 'Failed to fetch reports');
      } finally {
        setLoading(false);
      }
    };

    if (user?.department) {
      fetchReports();
    }
  }, [user?.department]);

  const getStatusBadge = (status) => {
    switch (status) {
      case 'pending':
        return <Badge bg="info" className="status-badge">Pending</Badge>;
      case 'verified':
        return <Badge bg="primary" className="status-badge">Verified</Badge>;
      case 'assigned':
        return <Badge bg="warning" className="status-badge">Assigned</Badge>;
      case 'in_progress':
        return <Badge bg="secondary" className="status-badge">In Progress</Badge>;
      case 'resolved':
        return <Badge bg="success" className="status-badge">Resolved</Badge>;
      case 'fake':
        return <Badge bg="danger" className="status-badge">Fake</Badge>;
      default:
        return <Badge bg="secondary">{status}</Badge>;
    }
  };

  const getCategoryIcon = (category) => {
    switch (category) {
      case 'electric':
        return 'fas fa-bolt';
      case 'water':
        return 'fas fa-tint';
      case 'road':
        return 'fas fa-road';
      case 'building':
        return 'fas fa-building';
      default:
        return 'fas fa-exclamation-triangle';
    }
  };

  const getPriorityColor = (priority) => {
    switch (priority) {
      case 'high':
        return '#dc3545';
      case 'medium':
        return '#fd7e14';
      case 'low':
        return '#28a745';
      default:
        return '#6c757d';
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'short', day: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const filteredReports = filter === 'all' 
    ? reports 
    : reports.filter(report => report.status === filter);

  return (
    <div className="all-reports-container" style={{ 
      minHeight: '100vh', 
      background: 'linear-gradient(135deg, #f5f7fa 0%, #c3cfe2 100%)',
      position: 'relative',
      overflow: 'hidden'
    }}>
      {/* Animated Background Elements */}
      <div className="bg-animation">
        <div className="shape shape-1"></div>
        <div className="shape shape-2"></div>
        <div className="shape shape-3"></div>
      </div>

      <Container className="py-5 position-relative" style={{ zIndex: 10 }}>
        {/* Header Section */}
        <Row className="mb-4">
          <Col>
            <div className="text-center mb-4">
              <h1 className="display-4 fw-bold text-white mb-3">All Reports</h1>
              <p className="lead text-white-50">View and manage reports in {user?.department} department</p>
            </div>
          </Col>
        </Row>

        {/* Filter Tabs */}
        <Row className="mb-4">
          <Col>
            <div className="d-flex justify-content-center">
              <div className="filter-tabs bg-white rounded-pill p-1 shadow-lg">
                <Button
                  variant={filter === 'all' ? 'primary' : 'link'}
                  className={`rounded-pill mx-1 ${filter === 'all' ? 'active-tab' : ''}`}
                  onClick={() => setFilter('all')}
                >
                  All ({reports.length})
                </Button>
                <Button
                  variant={filter === 'pending' ? 'primary' : 'link'}
                  className={`rounded-pill mx-1 ${filter === 'pending' ? 'active-tab' : ''}`}
                  onClick={() => setFilter('pending')}
                >
                  Pending ({reports.filter(r => r.status === 'pending').length})
                </Button>
                <Button
                  variant={filter === 'verified' ? 'primary' : 'link'}
                  className={`rounded-pill mx-1 ${filter === 'verified' ? 'active-tab' : ''}`}
                  onClick={() => setFilter('verified')}
                >
                  Verified ({reports.filter(r => r.status === 'verified').length})
                </Button>
                <Button
                  variant={filter === 'assigned' ? 'primary' : 'link'}
                  className={`rounded-pill mx-1 ${filter === 'assigned' ? 'active-tab' : ''}`}
                  onClick={() => setFilter('assigned')}
                >
                  Assigned ({reports.filter(r => r.status === 'assigned').length})
                </Button>
                <Button
                  variant={filter === 'in_progress' ? 'primary' : 'link'}
                  className={`rounded-pill mx-1 ${filter === 'in_progress' ? 'active-tab' : ''}`}
                  onClick={() => setFilter('in_progress')}
                >
                  In Progress ({reports.filter(r => r.status === 'in_progress').length})
                </Button>
                <Button
                  variant={filter === 'resolved' ? 'primary' : 'link'}
                  className={`rounded-pill mx-1 ${filter === 'resolved' ? 'active-tab' : ''}`}
                  onClick={() => setFilter('resolved')}
                >
                  Resolved ({reports.filter(r => r.status === 'resolved').length})
                </Button>
              </div>
            </div>
          </Col>
        </Row>

        {/* Stats Cards */}
        <Row className="mb-4">
          <Col md={3} className="mb-3">
            <Card className="h-100 border-0 shadow-lg stat-card">
              <Card.Body className="text-center">
                <div className="stat-icon bg-primary bg-gradient text-white rounded-circle mx-auto mb-3">
                  <i className="fas fa-clipboard-list fa-2x"></i>
                </div>
                <h3 className="fw-bold">{reports.length}</h3>
                <p className="text-muted">Total Reports</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3} className="mb-3">
            <Card className="h-100 border-0 shadow-lg stat-card">
              <Card.Body className="text-center">
                <div className="stat-icon bg-info bg-gradient text-white rounded-circle mx-auto mb-3">
                  <i className="fas fa-clock fa-2x"></i>
                </div>
                <h3 className="fw-bold">{reports.filter(r => r.status === 'pending').length}</h3>
                <p className="text-muted">Pending</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3} className="mb-3">
            <Card className="h-100 border-0 shadow-lg stat-card">
              <Card.Body className="text-center">
                <div className="stat-icon bg-warning bg-gradient text-white rounded-circle mx-auto mb-3">
                  <i className="fas fa-spinner fa-2x"></i>
                </div>
                <h3 className="fw-bold">{reports.filter(r => r.status === 'in_progress').length}</h3>
                <p className="text-muted">In Progress</p>
              </Card.Body>
            </Card>
          </Col>
          <Col md={3} className="mb-3">
            <Card className="h-100 border-0 shadow-lg stat-card">
              <Card.Body className="text-center">
                <div className="stat-icon bg-success bg-gradient text-white rounded-circle mx-auto mb-3">
                  <i className="fas fa-check-circle fa-2x"></i>
                </div>
                <h3 className="fw-bold">{reports.filter(r => r.status === 'resolved').length}</h3>
                <p className="text-muted">Resolved</p>
              </Card.Body>
            </Card>
          </Col>
        </Row>

        {/* Reports List */}
        <Row>
          <Col>
            {loading ? (
              <div className="d-flex justify-content-center py-5">
                <div className="text-center">
                  <Spinner animation="border" variant="primary" className="mb-3" />
                  <p className="text-white">Loading reports...</p>
                </div>
              </div>
            ) : error ? (
              <Alert variant="danger" className="mb-4">{error}</Alert>
            ) : filteredReports.length === 0 ? (
              <Card className="border-0 shadow-lg">
                <Card.Body className="text-center py-5">
                  <i className="fas fa-inbox fa-3x text-muted mb-3"></i>
                  <h4>No reports found</h4>
                  <p className="text-muted">
                    {filter === 'all' 
                      ? "There are no reports in your department yet." 
                      : `There are no ${filter} reports in your department.`
                    }
                  </p>
                  <Button 
                    variant="primary" 
                    className="mt-3 rounded-pill px-4"
                    as={Link}
                    to="/report-form"
                  >
                    <i className="fas fa-plus me-2"></i>
                    Create New Report
                  </Button>
                </Card.Body>
              </Card>
            ) : (
              <div className="reports-grid">
                {filteredReports.map(report => (
                  <Card key={report._id} className="border-0 shadow-lg mb-4 report-card">
                    <Card.Header className="bg-white border-0 pb-0">
                      <div className="d-flex justify-content-between align-items-start">
                        <div className="d-flex align-items-center">
                          <div className="category-icon rounded-circle bg-light text-primary me-3">
                            <i className={getCategoryIcon(report.category)}></i>
                          </div>
                          <div>
                            <Card.Title className="mb-1">{report.title}</Card.Title>
                            <Card.Subtitle className="text-muted">
                              <i className="fas fa-map-marker-alt me-1"></i>
                              {report.location} â€¢ {report.category}
                            </Card.Subtitle>
                          </div>
                        </div>
                        <div className="d-flex flex-column align-items-end">
                          {getStatusBadge(report.status)}
                          {report.priority && (
                            <div 
                              className="priority-dot mt-2"
                              style={{ backgroundColor: getPriorityColor(report.priority) }}
                              title={`Priority: ${report.priority}`}
                            ></div>
                          )}
                        </div>
                      </div>
                    </Card.Header>
                    <Card.Body>
                      <Card.Text>
                        {report.description.length > 150 
                          ? `${report.description.substring(0, 150)}...` 
                          : report.description
                        }
                      </Card.Text>
                      <div className="d-flex justify-content-between align-items-center mt-3">
                        <small className="text-muted">
                          <i className="far fa-calendar me-1"></i>
                          {formatDate(report.createdAt)}
                        </small>
                        <small className="text-muted">
                          <i className="far fa-user me-1"></i>
                          {report.reporterName}
                        </small>
                      </div>
                    </Card.Body>
                    <Card.Footer className="bg-white border-0 pt-0">
                      <div className="d-flex justify-content-between">
                        <small className="text-muted">
                          Report ID: {report._id}
                        </small>
                        <Button
                          variant="outline-primary"
                          size="sm"
                          className="rounded-pill view-details-btn"
                          as={Link}
                          to={`/reports/${report._id}`}
                        >
                          View Details
                          <i className="fas fa-arrow-right ms-1"></i>
                        </Button>
                      </div>
                    </Card.Footer>
                  </Card>
                ))}
              </div>
            )}
          </Col>
        </Row>
      </Container>

      <style jsx>{`
        .all-reports-container {
          font-family: 'Inter', sans-serif;
        }

        .bg-animation {
          position: absolute;
          top: 0;
          left: 0;
          width: 100%;
          height: 100%;
          overflow: hidden;
          z-index: 1;
        }

        .shape {
          position: absolute;
          border-radius: 50%;
          opacity: 0.1;
        }

        .shape-1 {
          width: 300px;
          height: 300px;
          background-color: #80ACC2;
          top: -150px;
          right: -150px;
          animation: float 20s infinite ease-in-out;
        }

        .shape-2 {
          width: 200px;
          height: 200px;
          background-color: #4a6b7a;
          bottom: -100px;
          left: -100px;
          animation: float 15s infinite ease-in-out;
        }

        .shape-3 {
          width: 150px;
          height: 150px;
          background-color: #b8d1e0;
          top: 50%;
          left: -75px;
          animation: float 25s infinite ease-in-out;
        }

        @keyframes float {
          0%, 100% { transform: translateY(0) rotate(0deg); }
          50% { transform: translateY(-20px) rotate(5deg); }
        }

        .filter-tabs {
          box-shadow: 0 10px 30px rgba(0, 0, 0, 0.1);
        }

