import React, { useState, useEffect } from 'react';
import { Navbar, Nav, Button, Container } from 'react-bootstrap';
import { Link, useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Header = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 20) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };

    window.addEventListener('scroll', handleScroll);
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  return (
    <>
      {/* Background tint layer for contrast */}
      <div 
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '80px',
          backgroundColor: 'rgba(244, 248, 250, 0.7)',
          zIndex: 998,
          transition: 'all 0.3s ease'
        }}
      ></div>

      {/* Glass Header */}
      <div 
        className={`glass-header ${scrolled ? 'scrolled' : ''}`}
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          zIndex: 999,
          backgroundColor: 'rgba(244, 248, 250, 0.6)',
          backdropFilter: 'blur(12px)',
          WebkitBackdropFilter: 'blur(12px)',
          borderBottom: '1px solid rgba(128, 172, 194, 0.2)',
          boxShadow: '0 4px 20px rgba(0, 0, 0, 0.08)',
          transition: 'all 0.3s ease'
        }}
      >
        <Container>
          <Navbar expand="lg" className="py-3" style={{ backgroundColor: 'transparent' }}>
            <Navbar.Brand 
              as={Link} 
              to="/" 
              className="d-flex align-items-center"
              style={{ 
                color: '#F9FBFC', 
                fontWeight: '600',
                fontSize: '1.25rem',
                textDecoration: 'none',
                fontFamily: 'Inter, sans-serif'
              }}
            >
              <div 
                className="d-flex align-items-center justify-content-center me-2"
                style={{
                  width: '40px',
                  height: '40px',
                  borderRadius: '50%',
                  backgroundColor: 'rgba(128, 172, 194, 0.15)',
                  boxShadow: '0 4px 8px rgba(128, 172, 194, 0.2)'
                }}
              >
                <i className="fas fa-university"></i>
              </div>
              Campus Issue Reporter
            </Navbar.Brand>
            <Navbar.Toggle 
              aria-controls="basic-navbar-nav" 
              style={{
                borderColor: 'rgba(128, 172, 194, 0.3)',
                backgroundColor: 'rgba(128, 172, 194, 0.1)'
              }}
            />
            <Navbar.Collapse id="basic-navbar-nav">
              <Nav className="mx-auto">
                <Nav.Link 
                  as={Link} 
                  to="/reports" 
                  className="nav-link-glass"
                  style={{ color: '#F9FBFC', fontWeight: '500', fontFamily: 'Inter, sans-serif' }}
                >
                  Reports
                </Nav.Link>
                <Nav.Link 
                  as={Link} 
                  to="/report-form" 
                  className="nav-link-glass"
                  style={{ color: '#F9FBFC', fontWeight: '500', fontFamily: 'Inter, sans-serif' }}
                >
                  New Report
                </Nav.Link>
                {user && (
                  <Nav.Link 
                    as={Link} 
                    to={
                      user.role === 'user' ? '/user-dashboard' :
                      user.role === 'maintainer' ? '/maintainer-dashboard' :
                      user.role === 'fieldhead' || user.role === 'field_head' ? '/fieldhead-dashboard' : '/'
                    } 
                    className="nav-link-glass"
                    style={{ color: '#F9FBFC', fontWeight: '500', fontFamily: 'Inter, sans-serif' }}
                  >
                    Dashboard
                  </Nav.Link>
                )}
              </Nav>
              <Nav>
                {user ? (
                  <div className="d-flex align-items-center">
                    <div 
                      className="d-flex align-items-center me-3 user-info"
                      style={{
                        padding: '8px 16px',
                        borderRadius: '30px',
                        backgroundColor: 'rgba(128, 172, 194, 0.1)',
                        backdropFilter: 'blur(5px)',
                        WebkitBackdropFilter: 'blur(5px)',
                        boxShadow: '0 4px 8px rgba(128, 172, 194, 0.15)'
                      }}
                    >
                      <i className="fas fa-user me-2" style={{ color: '#F9FBFC' }}></i>
                      <span style={{ color: '#F9FBFC', fontWeight: '500', fontFamily: 'Inter, sans-serif' }}>
                        {user.name}
                      </span>
                    </div>
                    <Button 
                      variant="outline-light" 
                      onClick={handleLogout}
                      className="glass-button"
                      style={{
                        borderColor: 'rgba(128, 172, 194, 0.3)',
                        color: '#F9FBFC',
                        fontWeight: '500',
                        borderRadius: '30px',
                        padding: '8px 20px',
                        backgroundColor: 'rgba(128, 172, 194, 0.1)',
                        backdropFilter: 'blur(5px)',
                        WebkitBackdropFilter: 'blur(5px)',
                        transition: 'all 0.3s ease',
                        fontFamily: 'Inter, sans-serif'
                      }}
                    >
                      <i className="fas fa-sign-out-alt me-2"></i>
                      Logout
                    </Button>
                  </div>
                ) : (
                  <>
                    <Button 
                      variant="outline-light" 
                      as={Link}
                      to="/login"
                      className="glass-button me-2"
                      style={{
                        borderColor: 'rgba(128, 172, 194, 0.3)',
                        color: '#F9FBFC',
                        fontWeight: '500',
                        borderRadius: '30px',
                        padding: '8px 20px',
                        backgroundColor: 'rgba(128, 172, 194, 0.1)',
                        backdropFilter: 'blur(5px)',
                        WebkitBackdropFilter: 'blur(5px)',
                        transition: 'all 0.3s ease',
                        fontFamily: 'Inter, sans-serif'
                      }}
                    >
                      <i className="fas fa-sign-in-alt me-2"></i>
                      Login
                    </Button>
                    <Button 
                      variant="outline-light" 
                      as={Link}
                      to="/register"
                      className="glass-button"
                      style={{
                        borderColor: 'rgba(128, 172, 194, 0.3)',
                        color: '#F9FBFC',
                        fontWeight: '500',
                        borderRadius: '30px',
                        padding: '8px 20px',
                        backgroundColor: 'rgba(128, 172, 194, 0.1)',
                        backdropFilter: 'blur(5px)',
                        WebkitBackdropFilter: 'blur(5px)',
                        transition: 'all 0.3s ease',
                        fontFamily: 'Inter, sans-serif'
                      }}
                    >
                      <i className="fas fa-user-plus me-2"></i>
                      Register
                    </Button>
                  </>
                )}
              </Nav>
            </Navbar.Collapse>
          </Navbar>
        </Container>
      </div>

      {/* Spacer to prevent content from hiding under fixed header */}
      <div style={{ height: '80px' }}></div>

      <style jsx>{`
        .nav-link-glass {
          position: relative;
          transition: all 0.3s ease;
          border-radius: 8px;
          padding: 8px 16px;
          margin: 0 5px;
          font-family: 'Inter', sans-serif;
        }

        .nav-link-glass::after {
          content: '';
          position: absolute;
          bottom: 0;
          left: 50%;
          width: 0;
          height: 2px;
          background-color: #80ACC2;
          transition: all 0.3s ease;
          transform: translateX(-50%);
        }

        .nav-link-glass:hover {
          background-color: rgba(128, 172, 194, 0.15);
          transform: translateY(-2px);
        }

        .nav-link-glass:hover::after {
          width: 100%;
          box-shadow: 0 0 10px rgba(128, 172, 194, 0.5);
        }

        .nav-link-glass.active {
          background-color: rgba(128, 172, 194, 0.2);
        }

        .nav-link-glass.active::after {
          width: 100%;
        }

        .glass-header.scrolled {
          background-color: rgba(244, 248, 250, 0.8);
          backdrop-filter: blur(15px);
          -webkit-backdrop-filter: blur(15px);
          box-shadow: 0 4px 20px rgba(0, 0, 0, 0.12);
        }

        .glass-button {
          position: relative;
          overflow: hidden;
        }

        .glass-button::before {
          content: '';
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.2), transparent);
          transition: left 0.5s;
        }

        .glass-button:hover {
          transform: translateY(-2px);
          box-shadow: 0 5px 15px rgba(128, 172, 194, 0.3);
          border-color: rgba(128, 172, 194, 0.5);
        }

        .glass-button:hover::before {
          left: 100%;
        }

        .user-info {
          position: relative;
          overflow: hidden;
        }

        .user-info::before {
          content: '';
          position: absolute;
          top: 0;
          left: -100%;
          width: 100%;
          height: 100%;
          background: linear-gradient(90deg, transparent, rgba(128, 172, 194, 0.2), transparent);
          transition: left 0.5s;
        }

        .user-info:hover::before {
          left: 100%;
        }

        .navbar-toggler {
          transition: all 0.3s ease;
        }

        .navbar-toggler:hover {
          background-color: rgba(128, 172, 194, 0.2) !important;
        }

        .navbar-toggler:focus {
          box-shadow: 0 0 0 0.2rem rgba(128, 172, 194, 0.25);
        }
      `}</style>
    </>
  );
};

export default Header;
