import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';

const Login = () => {
  const [collegeId, setCollegeId] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('user');
  const [error, setError] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const [submitted, setSubmitted] = useState(false);
  const [showPassword, setShowPassword] = useState(false);
  const { login } = useAuth();

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (submitted || loading) return;

    setError('');
    setMessage('');
    setLoading(true);
    setSubmitted(true);

    try {
      const response = await api.post('/auth/login', { collegeId, password, role });
      login(response.data.token, response.data.user);
      setMessage('Login successful');
      setLoading(false);
      // Redirect will be handled by the AuthContext
    } catch (error) {
      const status = error.response?.status;
      if (status === 404 || status === 401) {
        setError(error.response?.data?.error || 'Invalid credentials');
        setSubmitted(false);
      } else {
        setError(error.response?.data?.error || 'Failed to login');
        setSubmitted(false);
      }
      setLoading(false);
    }
  };

  const handleInputChange = (setter) => (e) => {
    setter(e.target.value);
    if (error) setError('');
  };

  return (
    <div className="min-h-screen organic-pattern">
      {/* Navigation Bar */}
      <nav className="bg-opacity-90 backdrop-blur-sm" style={{ backgroundColor: 'var(--primary-medium)' }}>
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16">
            <div className="flex items-center">
              <div className="flex-shrink-0 flex items-center">
                <i className="fas fa-university text-white text-xl mr-2"></i>
                <span className="text-white text-lg font-medium">Campus Issue Reporter</span>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <Link to="/login" className="text-white hover:text-gray-200 px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200">
                <i className="fas fa-sign-in-alt mr-1"></i> Login
              </Link>
              <Link to="/register" className="organic-border px-4 py-2 text-sm font-medium transition-all duration-200" style={{ backgroundColor: 'var(--light)', color: 'var(--primary-dark)' }}>
                <i className="fas fa-user-plus mr-1"></i> Register
              </Link>
            </div>
          </div>
        </div>
      </nav>

      {/* Main Content */}
      <div className="flex flex-col md:flex-row min-h-[calc(100vh-8rem)]">
        {/* Left Side - SVG Icon */}
        <div className="md:w-1/2 flex items-center justify-center p-8 organic-texture">
          <div className="text-center">
            <div className="organic-animation mb-8">
              <svg width="280" height="280" viewBox="0 0 280 280" fill="none" xmlns="http://www.w3.org/2000/svg">
                <path d="M140 30C165.391 30 189.978 38.9526 209.013 55.4864C228.047 72.0202 240.724 94.7321 244.595 119.833C248.466 144.934 243.281 170.617 230.018 192.312C216.755 214.007 196.331 230.355 172.336 238.637C148.34 246.919 122.257 246.642 98.4398 237.851C74.6225 229.06 54.5484 212.285 41.7533 190.312C28.9582 168.339 24.3175 142.564 28.7383 117.562C33.1591 92.5603 46.3352 70.1337 65.7186 54.0204C85.102 37.9071 109.889 29.4815 135.277 30H140Z" stroke="var(--accent)" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round" strokeDasharray="5 5"/>
                <path d="M140 70C155.913 70 171.174 76.3214 182.426 87.5736C193.679 98.8258 200 114.087 200 130C200 145.913 193.679 161.174 182.426 172.426C171.174 183.679 155.913 190 140 190C124.087 190 108.826 183.679 97.5736 172.426C86.3214 161.174 80 145.913 80 130C80 114.087 86.3214 98.8258 97.5736 87.5736C108.826 76.3214 124.087 70 140 70Z" stroke="var(--light)" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M140 100C148.837 100 157.319 103.68 163.319 109.181C169.32 114.681 173 122.663 173 131C173 139.337 169.32 147.319 163.319 152.819C157.319 158.32 148.837 162 140 162C131.163 162 122.681 158.32 116.681 152.819C110.68 147.319 107 139.337 107 131C107 122.663 110.68 114.681 116.681 109.181C122.681 103.68 131.163 100 140 100Z" stroke="var(--primary-medium)" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M140 120C144.418 120 148.647 121.68 151.734 124.667C154.821 127.653 156.556 131.749 156.556 136C156.556 140.251 154.821 144.347 151.734 147.333C148.647 150.32 144.418 152 140 152C135.582 152 131.353 150.32 128.266 147.333C125.179 144.347 123.444 140.251 123.444 136C123.444 131.749 125.179 127.653 128.266 124.667C131.353 121.68 135.582 120 140 120Z" fill="var(--accent)"/>
                <path d="M140 125C142.209 125 144.328 125.842 145.893 127.327C147.458 128.812 148.345 130.822 148.345 132.917C148.345 135.011 147.458 137.021 145.893 138.506C144.328 139.991 142.209 140.833 140 140.833C137.791 140.833 135.672 139.991 134.107 138.506C132.542 137.021 131.655 135.011 131.655 132.917C131.655 130.822 132.542 128.812 134.107 127.327C135.672 125.842 137.791 125 140 125Z" fill="var(--light)"/>
                <path d="M90 200C90 200 110 220 140 220C170 220 190 200 190 200" stroke="var(--accent)" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M110 210V230" stroke="var(--light)" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
                <path d="M170 210V230" stroke="var(--light)" strokeWidth="3" strokeLinecap="round" strokeLinejoin="round"/>
              </svg>
            </div>
            <h1 className="text-3xl font-bold mb-4 hand-drawn" style={{ color: 'var(--light)' }}>Campus Issue Reporter</h1>
            <p className="text-lg mb-6" style={{ color: 'var(--accent)' }}>Help improve your campus experience</p>
            <div className="organic-line mb-6"></div>
            <div className="flex justify-center space-x-8">
              <div className="text-center">
                <i className="fas fa-bug text-2xl mb-2" style={{ color: 'var(--light)' }}></i>
                <p className="text-sm" style={{ color: 'var(--light)' }}>Report Issues</p>
              </div>
              <div className="text-center">
                <i className="fas fa-chart-line text-2xl mb-2" style={{ color: 'var(--light)' }}></i>
                <p className="text-sm" style={{ color: 'var(--light)' }}>Track Progress</p>
              </div>
              <div className="text-center">
                <i className="fas fa-comments text-2xl mb-2" style={{ color: 'var(--light)' }}></i>
                <p className="text-sm" style={{ color: 'var(--light)' }}>Get Updates</p>
              </div>
            </div>
          </div>
        </div>

        {/* Right Side - Login Form */}
        <div className="md:w-1/2 flex items-center justify-center p-8">
          <div className="max-w-md w-full space-y-6">
            {/* Login Form */}
            <div className="organic-border organic-shadow p-8" style={{ backgroundColor: 'var(--primary-medium)' }}>
              <div className="text-center mb-6">
                <div className="hand-drawn-circle mx-auto h-16 w-16 flex items-center justify-center mb-4" style={{ backgroundColor: 'var(--accent)' }}>
                  <i className="fas fa-clipboard-list text-white text-2xl"></i>
                </div>
                <h2 className="text-2xl font-bold" style={{ color: 'var(--light)' }}>Sign In</h2>
              </div>

              <form onSubmit={handleSubmit} className="space-y-5">
                {/* Error/Message Display */}
                {error && (
                  <div className="p-3 rounded-lg text-sm text-white bg-red-500 error-shake" style={{ backgroundColor: '#e74c3c' }}>
                    <i className="fas fa-exclamation-circle mr-2"></i>
                    {error}
                  </div>
                )}
                {message && (
                  <div className="p-3 rounded-lg text-sm" style={{ backgroundColor: 'var(--accent)', color: 'var(--primary-dark)' }}>
                    <i className="fas fa-check-circle mr-2"></i>
                    {message}
                  </div>
                )}

                {/* College ID Input */}
                <div>
                  <label htmlFor="collegeId" className="block text-sm font-medium mb-1" style={{ color: 'var(--light)' }}>
                    <i className="fas fa-id-card mr-1"></i> College ID
                  </label>
                  <input
                    id="collegeId"
                    type="text"
                    value={collegeId}
                    onChange={handleInputChange(setCollegeId)}
                    required
                    className={`input-organic w-full px-4 py-3 border-0 text-gray-900 focus:outline-none focus:ring-2 focus:ring-offset-2 ${error ? 'border-red-500' : ''}`}
                    style={{ backgroundColor: 'var(--light)' }}
                    placeholder="Enter your college ID"
                  />
                </div>

                {/* Password Input */}
                <div>
                  <label htmlFor="password" className="block text-sm font-medium mb-1" style={{ color: 'var(--light)' }}>
                    <i className="fas fa-lock mr-1"></i> Password
                  </label>
                  <div className="relative">
                    <input
                      id="password"
                      type={showPassword ? 'text' : 'password'}
                      value={password}
                      onChange={handleInputChange(setPassword)}
                      required
                      className={`input-organic w-full px-4 py-3 pr-10 border-0 text-gray-900 focus:outline-none focus:ring-2 focus:ring-offset-2 ${error ? 'border-red-500' : ''}`}
                      style={{ backgroundColor: 'var(--light)' }}
                      placeholder="Enter your password"
                    />
                    <button
                      type="button"
                      onClick={() => setShowPassword(!showPassword)}
                      className="absolute inset-y-0 right-0 pr-3 flex items-center"
                    >
                      <i className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'}`} style={{ color: 'var(--primary-dark)' }}></i>
                    </button>
                  </div>
                </div>

                {/* Login As Dropdown */}
                <div>
                  <label htmlFor="role" className="block text-sm font-medium mb-1" style={{ color: 'var(--light)' }}>
                    <i className="fas fa-user-tag mr-1"></i> Login As
                  </label>
                  <select
                    id="role"
                    value={role}
                    onChange={handleInputChange(setRole)}
